mapper.select:
and (t.client_id like concat('%', #{item}, '%') )
and t.month_manufacture_plan_sid =#{monthManufacturePlanSid}
and t.month_manufacture_plan_sid in (#{monthManufacturePlanSid})
and t.plant_sid =#{plantSid}
and t.plant_sid in (#{plantSid})
and t.department in (#{department})
and t.yearmonth =#{yearmonth}
and t.yearmonth >= #{yearMonthBeginTime}
and t.yearmonth <= #{yearMonthEndTime}
and (t.business_type like concat('%', #{item}, '%') )
and t.company_sid =#{companySid}
and (t.refer_doc_category like concat('%', #{item}, '%') )
and t.month_manufacture_plan_code like concat('%', #{monthManufacturePlanCode}, '%')
and (t.handle_status like concat('%', #{item}, '%') )
and t.handle_status in (#{handleStatus})
and (t.creator_account like concat('%', #{item}, '%') )
and t.creator_account in ( #{creatorAccount})
and date_format(t.create_date,'%y%m%d') >= date_format(#{beginTime},'%y%m%d')
and date_format(t.create_date,'%y%m%d') <= date_format(#{endTime},'%y%m%d')
and (t.updater_account like concat('%', #{item}, '%') )
and t.update_date = #{updateDate}
and (t.confirmer_account like concat('%', #{item}, '%') )
and t.confirm_date = #{confirmDate}
and (t.data_source_sys like concat('%', #{item}, '%') )
${params.dataScope}
order by t.month_manufacture_plan_code desc, t.create_date desc

mapper.update:
update s_man_month_manufacture_plan
plant_sid = #{plantSid}, plant_code = #{plantCode}, yearmonth = #{yearmonth}, business_type = #{businessType}, company_sid = #{companySid}, refer_doc_category = #{referDocCategory}, process_route_sid = #{processRouteSid}, remark = #{remark}, process_route_code = #{processRouteCode}, handle_status = #{handleStatus}, concern_task_group_sid = #{concernTaskGroupSid}, concern_task_group_code = #{concernTaskGroupCode}, month_remark = #{monthRemark}, updater_account = #{updaterAccount}, update_date = #{updateDate}, confirmer_account = #{confirmerAccount}, confirm_date = #{confirmDate}, department = #{department},
where month_manufacture_plan_sid = #{monthManufacturePlanSid}

update.foreach:
update s_man_month_manufacture_plan
client_id = #{clientId}, month_manufacture_plan_code = #{monthManufacturePlanCode}, plant_sid = #{plantSid}, document_type = #{documentType}, business_type = #{businessType}, company_sid = #{companySid}, refer_doc_category = #{referDocCategory}, remark = #{remark}, handle_status = #{handleStatus}, creator_account = #{creatorAccount}, create_date = #{createDate}, updater_account = #{updaterAccount}, update_date = #{updateDate}, confirmer_account = #{confirmerAccount}, confirm_date = #{confirmDate}, data_source_sys = #{dataSourceSys},
where month_manufacture_plan_sid = #{monthManufacturePlanSid}
