mapper.select:
and t.client_id = #{clientId}
and t.plant_sid = #{plantSid}
and t.plant_sid in ( #{item})
and t.process_route_sid = #{processRouteSid}
and t.process_route_sid in ( #{processRouteSid})
and (t.process_route_code like concat('%', #{item}, '%'))
and (t.process_route_name like concat('%', #{item}, '%'))
and ( t.process_route_category like concat('%', #{item}, '%') )
and t.process_route_category in ( #{processRouteCategory})
and t.customer_sid in ( #{customer})
and t.company_sid in ( #{customer})
and t.creator_account in ( #{creatorAccount})
and t.effective_date = #{effectiveDate}
and (t.useable_type like concat('%', #{item}, '%') )
and t.company_sid =#{companySid}
and t.status = #{status}
and t.handle_status = #{handleStatus}
and t.handle_status in (#{handleStatus})
and (t.creator_account like concat('%', #{item}, '%') )
and date_format(t.create_date,'%y%m%d') >= date_format(#{beginTime},'%y%m%d')
and date_format(t.create_date,'%y%m%d') <= date_format(#{endTime},'%y%m%d')
and (t.updater_account like concat('%', #{item}, '%') )
and t.update_date = #{updateDate}
and (t.confirmer_account like concat('%', #{item}, '%') )
and t.confirm_date = #{confirmDate}
and (t.data_source_sys like concat('%', #{item}, '%') )
${params.dataScope}
order by t.create_date desc, t.process_route_code desc

