mapper.select:



        and t.concern_task_code LIKE concat('%', #{concernTaskCode}, '%')


        and t4.concern_task_name LIKE concat('%', #{concernTaskName}, '%')


        and t3.concern_task_group_code like CONCAT("%" , #{concernTaskGroupCode} , "%")


        and t3.concern_task_group_name like concat('%', #{concernTaskGroupName}, '%')


        and t4.status = #{status}


        and t3.plant_sid in (

            #{plantSid}

        )


        and t3.handle_status in (

            #{handleStatus}

        )


        and t.creator_account in (

            #{item}

        )


        and date_format(t.create_date,'%y%m%d') >= date_format(#{beginTime},'%y%m%d')


        and date_format(t.create_date,'%y%m%d') <= date_format(#{endTime},'%y%m%d')


ORDER BY t.create_date  desc




        and t.client_id = #{clientId}


        and t.concern_task_group_sid = #{concernTaskGroupSid}


        and t.concern_task_sid = #{concernTaskSid}


        and t.concern_task_code = #{concernTaskCode}


        and t.handler_sid = #{handlerSid}


        and t.handler_code = #{handlerCode}


        and t.sort = #{sort}


        and t.creator_account = #{creatorAccount}


        and date_format(t.create_date,'%y%m%d') >= date_format(#{beginTime},'%y%m%d')


        and date_format(t.create_date,'%y%m%d') <= date_format(#{endTime},'%y%m%d')


        and t.updater_account = #{updaterAccount}


        and t.update_date = #{updateDate}


        and t.data_source_sys = #{dataSourceSys}


        and ${params.dataScope}


ORDER BY t.sort asc

