update s_man_produce_concern_task_group
client_id = #{clientId}, concern_task_group_code = #{concernTaskGroupCode}, concern_task_group_name = #{concernTaskGroupName}, plant_sid = #{plantSid}, plant_code = #{plantCode}, remark = #{remark}, handle_status = #{handleStatus}, updater_account = #{updaterAccount}, update_date = #{updateDate}, confirmer_account = #{confirmerAccount}, confirm_date = #{confirmDate},
where concern_task_group_sid = #{concernTaskGroupSid}

