mapper.select:



        and t.client_id = #{clientId}


        and t.concern_task_code LIKE concat('%', #{concernTaskCode}, '%')


        and t.concern_task_name LIKE concat('%', #{concernTaskName}, '%')


        and t.is_picture_upload LIKE concat('%', #{isPictureUpload}, '%')


        and t.is_attach_upload LIKE concat('%', #{isAttachUpload}, '%')


        and t.produce_stage = #{produceStage}


        and t.produce_stage in (

                #{produceStage}

        )


        and t.status = #{status}


        and t.handle_status = #{handleStatus}


        and t.handle_status in (

                #{handleStatus}

        )


        and t.concern_task_type in (

            #{item}

        )


        and t.creator_account = #{creatorAccount}


        and t2.nick_name like concat("%" , #{creatorAccountName} , "%")


        and date_format(t.create_date,'%y%m%d') >= date_format(#{beginTime},'%y%m%d')


        and date_format(t.create_date,'%y%m%d') <= date_format(#{endTime},'%y%m%d')


        and t.updater_account = #{updaterAccount}


        and t.update_date = #{updateDate}


        and t.confirmer_account = #{confirmerAccount}


        and t.confirm_date = #{confirmDate}


        and t.data_source_sys = #{dataSourceSys}


        ${params.dataScope}


order by t.create_date desc

mapper.update:
update s_man_produce_concern_task
client_id = #{clientId}, concern_task_code = #{concernTaskCode}, concern_task_name = #{concernTaskName}, is_picture_upload = #{isPictureUpload}, is_attach_upload = #{isAttachUpload}, produce_stage = #{produceStage}, remark = #{remark}, status = #{status}, handle_status = #{handleStatus}, updater_account = #{updaterAccount}, update_date = #{updateDate}, confirmer_account = #{confirmerAccount}, confirm_date = #{confirmDate}, concern_task_type=#{concernTaskType},
where concern_task_sid = #{concernTaskSid}

update.foreach:
update s_man_produce_concern_task
client_id = #{clientId}, concern_task_code = #{concernTaskCode}, concern_task_name = #{concernTaskName}, is_picture_upload = #{isPictureUpload}, is_attach_upload = #{isAttachUpload}, produce_stage = #{produceStage}, remark = #{remark}, status = #{status}, handle_status = #{handleStatus}, updater_account = #{updaterAccount}, update_date = #{updateDate}, confirmer_account = #{confirmerAccount}, confirm_date = #{confirmDate},
where concern_task_sid = #{concernTaskSid}
