mapper.select:



        and creator_account_name like concat("%" , #{creatorAccountName} , "%")


        and t.client_id = #{clientId}


        and t.plant_sid in
        (#{item})


        and t.department in
        (#{item})


        and t.work_center_sid in
        (#{item})


        and t.plant_code = #{plantCode}


        and t.department like concat("%" , #{department} , "%")


        and t.work_center_sid = #{workCenterSid}


        and t.work_center_code = #{workCenterCode}


        and t.defect_source = #{defectSource}


        and t.manufacture_order_sid = #{manufactureOrderSid}


        and t.manufacture_order_code like concat("%" , #{manufactureOrderCode} , "%")


        and t.product_defect_code like concat("%" , #{productDefectCode} , "%")


        and t.product_sid = #{productSid}


        and t.product_code like concat("%" , #{productCode} , "%")


        and t.paichan_batch = #{paichanBatch}


        and t.defect_reporter_sid = #{defectReporterSid}


        and t.defect_reporter_code like concat("%" , #{defectReporterCode} , "%")


        and t.defect_report_date = #{defectReportDate}


        and t.plan_resolve_date = #{planResolveDate}


        and t.defect_priority = #{defectPriority}


        and t.defect_resolver_sid = #{defectResolverSid}


        and t.defect_resolver_code = #{defectResolverCode}


        and t.resolve_status = #{resolveStatus}


        and t.defect_class_sid = #{defectClassSid}


        and t.defect_class_code = #{defectClassCode}


        and t.defect_short_description = #{defectShortDescription}


        and t.solution_description = #{solutionDescription}


        and t.handle_status = #{handleStatus}


        and t.creator_account = #{creatorAccount}


        and t.creator_account in
        (#{item})


        and t.defect_source in
        (#{item})


        and t.resolve_status in
        (#{item})


        and t.defect_reporter_sid in
        (#{item})


        and t.defect_resolver_sid in
        (#{item})


        and t.defect_priority in
        (#{item})


        and date_format(t.plan_resolve_date,'%y%m%d') >= date_format(#{planResolveDateBegin},'%y%m%d')


        and date_format(t.plan_resolve_date,'%y%m%d') <= date_format(#{planResolveDateEnd},'%y%m%d')



        and date_format(t.defect_report_date,'%y%m%d') >= date_format(#{defectReportDateBegin},'%y%m%d')


        and date_format(t.defect_report_date,'%y%m%d') <= date_format(#{defectReportDateEnd},'%y%m%d')


        and t.updater_account = #{updaterAccount}


        and t.update_date = #{updateDate}


        and t.confirmer_account = #{confirmerAccount}


        and t.confirm_date = #{confirmDate}


        and t.data_source_sys = #{dataSourceSys}


        ${params.dataScope}


order by  t.product_defect_code desc

