mapper.select:
select
t.quantity_fenpei
from s_man_week_manufacture_plan_item t
LEFT JOIN s_man_manufacture_order_process t2 ON t2.manufacture_order_process_sid = t.manufacture_order_process_sid
left join s_man_week_manufacture_plan t3 on t3.week_manufacture_plan_sid=t.week_manufacture_plan_sid

    t3.handle_status='5'

        and t.material_sid = #{materialSid}


        and t.work_center_sid = #{workCenterSid}


        and t.sku1_sid = #{sku1Sid}


        and t.sku1_sid is null


        and t.paichan_batch = #{paichanBatch}


        and t.paichan_batch is null


        and t2.process_sid = #{processSid}


        and t2.process_sid is null


 order by t3.date_start desc
 limit 1




        and (t.client_id like concat('%',
        #{item}, '%')
    )


        and t.week_manufacture_plan_sid =#{weekManufacturePlanSid}


        and t.manufacture_order_sid =#{manufactureOrderSid}


        and t.manufacture_order_code =#{manufactureOrderCode}


        and (t.unit_base like concat('%',
        #{item}, '%')
    )


        and t.item_num =#{itemNum}


        and (t.creator_account like
        concat('%',
        #{item}, '%')
    )


        and date_format(t.create_date,'%y%m%d') >= date_format(#{beginTime},'%y%m%d')


        and date_format(t.create_date,'%y%m%d') <= date_format(#{endTime},'%y%m%d')


        and (t.updater_account like
        concat('%',
        #{item}, '%')
    )


        and t.update_date = #{updateDate}


        and (t.data_source_sys like
        concat('%',
        #{item}, '%')
    )


        ${params.dataScope}


group by t.week_manufacture_plan_item_sid

mapper.update:
update s_man_week_manufacture_plan_item
client_id = #{clientId}, week_manufacture_plan_sid = #{weekManufacturePlanSid}, manufacture_order_sid = #{manufactureOrderSid}, manufacture_order_code = #{manufactureOrderCode}, manufacture_order_process_sid = #{manufactureOrderProcessSid}, unit_base = #{unitBase}, item_num = #{itemNum}, remark = #{remark}, updater_account = #{updaterAccount}, manufacture_order_process_item_num = #{manufactureOrderProcessItemNum}, update_date = #{updateDate}, work_center_sid = #{workCenterSid}, work_center_code = #{workCenterCode}, plan_start_date = #{planStartDate}, plan_end_date = #{planEndDate}, material_sid = #{materialSid}, material_code = #{materialCode}, paichan_batch = #{paichanBatch}, fenpei_chefengzu = #{fenpeiChefengzu}, start_date_chejian = #{startDateChejian}, plan_date_songyxh = #{planDateSongyxh}, plan_date_shouyxh = #{planDateShouyxh}, position_xiuyin = #{positionXiuyin}, plan_date_baodan = #{planDateBaodan}, plan_quantity_chuh = #{planQuantityChuh}, end_date_chejian = #{endDateChejian}, jindu_remark = #{jinduRemark}, jindu_remark_tg = #{jinduRemarkTg}, manufacture_order_process_code = #{manufactureOrderProcessCode}, jindu_remark_sp = #{jinduRemarkSp}, quantity_songxs = #{quantitySongxs}, quantity_shouxs = #{quantityShouxs}, sku1_sid = #{sku1Sid}, sku1_code = #{sku1Code}, sku1_type = #{sku1Type}, enter_dimension = #{enterDimension}, quantity_fenpei=#{quantityFenpei},
where week_manufacture_plan_item_sid = #{weekManufacturePlanItemSid}

update.foreach:
update s_man_week_manufacture_plan_item
client_id = #{clientId}, week_manufacture_plan_sid = #{weekManufacturePlanSid}, manufacture_order_sid = #{manufactureOrderSid}, manufacture_order_code = #{manufactureOrderCode}, material_sid = #{materialSid}, manufacture_order_process_sid = #{manufactureOrderProcessSid}, unit_base = #{unitBase}, plan_quantity = #{planQuantity}, plan_defective_quantity = #{planDefectiveQuantity}, plan_daily_output = #{planDailyOutput}, item_num = #{itemNum}, remark = #{remark}, creator_account = #{creatorAccount}, create_date = #{createDate}, updater_account = #{updaterAccount}, update_date = #{updateDate}, data_source_sys = #{dataSourceSys},
where week_manufacture_plan_item_sid = #{weekManufacturePlanItemSid}
