mapper.select:
SELECT t1.plant_sid,t1.plant_name,t1.plant_code,t2.work_center_sid,t2.work_center_name,t2.work_center_code,t.plan_start_date,t.plan_end_date FROM s_man_manufacture_order_process t LEFT JOIN s_bas_plant t1 on t1.plant_sid=t.plant_sid LEFT JOIN s_man_work_center t2 on t2.work_center_sid=t.work_center_sid
and t.work_center_sid =#{workCenterSid}
and t.plant_sid=#{plantSid}
and date_format(t.plan_end_date,'%y%m%d') >= date_format(#{beginTime},'%y%m%d')
and date_format(t.plan_start_date,'%y%m%d') <= date_format(#{endTime},'%y%m%d')




        and (t.client_id like concat('%',
        #{item}, '%')
    )


        and t.process_sids =#{processSids}


        and t.work_center_sid =#{workCenterSid}


        and t.work_center_sid in
        (#{workCenterSid})


        and (t.work_center_code like
        concat('%', #{item}, '%'))


        and (t.work_center_name like
        concat('%', #{item}, '%'))


        and (
        t.work_center_category like concat('%',
        #{item}, '%')
    )


        and t.work_center_category in
        (
        #{workCenterCategory})


        and t.company_sid =#{companySid}


        and t.plant_sid =#{plantSid}


        and t.department_sid =#{departmentSid}


        and t.plant_sid in
        (#{plantSid})


        and t.handle_status in
        (#{handleStatus})


        and t.department_sid in
        (#{department})


        and t.vendor_sid in
        (#{vendorSid})


        and t.work_center_type in
        (#{workCenterType})


        and t.creator_account in
        (#{creatorAccount})


        and (t.director like concat('%',
        #{item}, '%')
    )


        and (t.useable_type like
        concat('%',
        #{item}, '%')
    )


        and t.output =#{output}


        and t.deposit_area =#{depositArea}


        and (t.time_unit like concat('%',
        #{item}, '%')
    )


        and (t.unit like concat('%',
        #{item}, '%')
    )


        and (t.phone like concat('%',
        #{item}, '%')
    )


        and (t.address like concat('%',
        #{item}, '%')
    )


        and t.status = #{status}


        and t.handle_status = #{handleStatus}


        and (t.creator_account like
        concat('%',
        #{item}, '%')
    )


        and date_format(t.create_date,'%y%m%d') >= date_format(#{beginTime},'%y%m%d')


        and date_format(t.create_date,'%y%m%d') <= date_format(#{endTime},'%y%m%d')


        and (t.updater_account like
        concat('%',
        #{item}, '%')
    )


        and t.update_date = #{updateDate}


        and (t.confirmer_account
        like concat('%',
        #{item}, '%')
    )


        and t.confirm_date = #{confirmDate}


        and (t.data_source_sys like
        concat('%',
        #{item}, '%')
    )


        ${params.dataScope}


order by t2.plant_name asc, t.work_center_name asc, t.create_date desc

mapper.update:
update s_man_work_center
work_center_code = #{workCenterCode}, work_center_name = #{workCenterName}, work_center_category = #{workCenterCategory}, company_sid = #{companySid}, plant_sid = #{plantSid}, process_sids = #{processSids}, process_codes = #{processCodes}, director = #{director}, useable_type = #{useableType}, output = #{output}, deposit_area = #{depositArea}, time_unit = #{timeUnit}, unit = #{unit}, phone = #{phone}, address = #{address}, remark = #{remark}, status = #{status}, handle_status = #{handleStatus}, updater_account = #{updaterAccount}, update_date = #{updateDate}, confirmer_account = #{confirmerAccount}, confirm_date = #{confirmDate}, data_source_sys = #{dataSourceSys}, department_sid = #{departmentSid}, department_code = #{departmentCode}, work_center_type = #{workCenterType}, vendor_sid = #{vendorSid}, vendor_code = #{vendorCode},
where work_center_sid = #{workCenterSid}

update.foreach:
update s_man_work_center
client_id = #{clientId}, work_center_code = #{workCenterCode}, work_center_name = #{workCenterName}, work_center_category = #{workCenterCategory}, company_sid = #{companySid}, plant_sid = #{plantSid}, process_sids = #{processSids}, process_codes = #{processCodes}, director = #{director}, useable_type = #{useableType}, output = #{output}, deposit_area = #{depositArea}, time_unit = #{timeUnit}, unit = #{unit}, phone = #{phone}, address = #{address}, remark = #{remark}, status = #{status}, handle_status = #{handleStatus}, creator_account = #{creatorAccount}, create_date = #{createDate}, updater_account = #{updaterAccount}, update_date = #{updateDate}, confirmer_account = #{confirmerAccount}, confirm_date = #{confirmDate}, data_source_sys = #{dataSourceSys}, department_sid = #{departmentSid},
where work_center_sid = #{workCenterSid}

mapper.select:
select t.work_center_sid, t.work_center_code, t.work_center_name, t.creator_account, t.create_date, t.handle_status, t.department_code, t.department_sid, t.status, t.director, t1.staff_name as director_name, t2.name as department_name from s_man_work_center t left join s_bas_staff t1 on t.director = t1.staff_code left join s_con_manufacture_department t2 on t2.sid=t.department_sid
and t.handle_status = #{handleStatus}
and t.department_sid = #{departmentSid}
and t.status = #{status}
and t.plant_sid = #{plantSid}
order by t.work_center_name asc
