mapper.select:



        and (t.client_id
        like concat('%',
        #{item}, '%')
    )


        and t.work_center_member_sid =#{workCenterMemberSid}


        and t.work_center_sid =#{workCenterSid}


        and (t.member_code
        like concat('%',
        #{item}, '%')
    )


        and t.member_sid =#{memberSid}

    and
        t.member_name like concat('%', #{memberName}, '%')


        and t6.is_on_job =#{isOnJob}


        and t6.handle_status =#{handleStatus}


        and t.member_position =#{memberPosition}


        and (t.phone
        like concat('%',
        #{item}, '%')
    )


        and t.sort =#{sort}


        and (t.creator_account
        like concat('%',
        #{item}, '%')
    )


        and date_format(t.create_date,'%y%m%d') >= date_format(#{beginTime},'%y%m%d')


        and date_format(t.create_date,'%y%m%d') <= date_format(#{endTime},'%y%m%d')


        and (t.updater_account
        like concat('%',
        #{item}, '%')
    )


        and t.update_date = #{updateDate}


        and (t.data_source_sys
        like concat('%',
        #{item}, '%')
    )


        ${params.dataScope}


group by t.member_sid
order by t.member_name asc

mapper.update:
update s_man_work_center_member
client_id = #{clientId}, work_center_member_sid = #{workCenterMemberSid}, work_center_sid = #{workCenterSid}, member_code = #{memberCode}, member_sid = #{memberSid}, member_name = #{memberName}, member_position = #{memberPosition}, phone = #{phone}, sort = #{sort}, remark = #{remark}, updater_account = #{updaterAccount}, update_date = #{updateDate}, data_source_sys = #{dataSourceSys},
where work_center_member_sid = #{workCenterMemberSid}

update.foreach:
update s_man_work_center_member
client_id = #{clientId}, work_center_member_sid = #{workCenterMemberSid}, work_center_sid = #{workCenterSid}, member_code = #{memberCode}, member_sid = #{memberSid}, member_name = #{memberName}, member_position = #{memberPosition}, phone = #{phone}, sort = #{sort}, remark = #{remark}, updater_account = #{updaterAccount}, update_date = #{updateDate}, data_source_sys = #{dataSourceSys},
where work_center_member_sid = #{workCenterMemberSid}
