mapper.select:



        and (t.client_id like concat('%',
        #{item}, '%')
    )


        and t.step_complete_sid =#{stepCompleteSid}


        and t.step_complete_sid in
        (#{stepCompleteSid})


        and (t.step_complete_code
        like concat('%',
        #{item}, '%')
    )


        and t.product_sid =#{productSid}


        and (t.product_code
        like concat('%', #{item}, '%')
    )


        and (t10.material_name
        like concat('%', #{item}, '%')
    )


        and t.plant_sid =#{plantSid}


        and t.plant_sid in
        (#{plantSid})


        and t.reporter =#{reporter}


        and t.reporter in
        (#{reporter})


        and t.report_date = #{reportDate}


        and date_format(t.report_date,'%y%m%d') >= date_format(#{reportBeginDate},'%y%m%d')


        and date_format(t.report_date,'%y%m%d') <= date_format(#{reportEndDate},'%y%m%d')


        and t.report_cycle = #{reportCycle}


        and t.enter_mode = #{enterMode}


        and t.yearmonth = #{yearmonth}


        and t.department = #{department}


        and t.department in
        (#{department})


        and t.work_center_sid =#{workCenterSid}


        and t.work_center_sid in
        (#{workCenterSid})


        and t.product_price_type = #{productPriceType}


        and t.product_price_type in
        (#{productPriceType})


        and t.jixin_wangong_type = #{jixinWangongType}


        and t.jixin_wangong_type in
        (#{jixinWangongType})


        and (t.handle_status like
        concat('%',
        #{item}, '%')
    )


        and t.handle_status in
        (#{handleStatus})


        and (t.creator_account like
        concat('%',
        #{item}, '%')
    )


        and t.creator_account in
        (#{creatorAccount})


        and date_format(t.create_date,'%y%m%d') >= date_format(#{beginTime},'%y%m%d')


        and date_format(t.create_date,'%y%m%d') <= date_format(#{endTime},'%y%m%d')


        and (t.updater_account like
        concat('%',
        #{item}, '%')
    )


        and t.update_date = #{updateDate}


        and (t.confirmer_account
        like concat('%',
        #{item}, '%')
    )


        and t.confirm_date = #{confirmDate}


        and (t.data_source_sys like
        concat('%',
        #{item}, '%')
    )


        ${params.dataScope}


order by t.step_complete_code desc, t.create_date desc

