mapper.select:



        and (t.client_id like concat('%',
        #{item}, '%')
    )


        and t.process_step_sid =#{processStepSid}


        and t.process_step_sid in
        (#{processStepSid})


        and t.process_step_code like concat('%',#{processStepCode}, '%')

    and
        t.process_step_name like concat('%', #{processStepName}, '%')


        and t.step_category = #{stepCategory}


        and t.standard_price = #{standardPrice}


        and (t.currency like concat('%',
        #{item}, '%')
    )


        and (t.currency_unit like
        concat('%',
        #{item}, '%')
    )


        and (t.task_unit like concat('%',
        #{item}, '%')
    )


        and t.process_sid =#{processSid}


        and t.process_sid in
        (#{processSid})


        and (t.status like concat('%',
        #{item}, '%')
    )


        and (t.handle_status like
        concat('%',
        #{item}, '%')
    )


        and t.handle_status in
        (#{handleStatus})


        and (t2.nick_name like
        concat('%',
        #{item}, '%')
    )


        and (t.creator_account like
        concat('%',
        #{item}, '%')
    )


        and date_format(t.create_date,'%y%m%d') >= date_format(#{beginTime},'%y%m%d')


        and date_format(t.create_date,'%y%m%d') <= date_format(#{endTime},'%y%m%d')


        and (t.updater_account like
        concat('%',
        #{item}, '%')
    )


        and t.update_date = #{updateDate}


        and (t.confirmer_account
        like concat('%',
        #{item}, '%')
    )


        and t.confirm_date = #{confirmDate}


        and (t.data_source_sys like
        concat('%',
        #{item}, '%')
    )


        ${params.dataScope}


order by t.process_step_code desc

mapper.update:
update s_pay_process_step
process_step_sid = #{processStepSid}, process_step_code = #{processStepCode}, process_step_name = #{processStepName}, step_category = #{stepCategory}, standard_price = #{standardPrice}, currency = #{currency}, currency_unit = #{currencyUnit}, task_unit = #{taskUnit}, process_sid = #{processSid}, remark = #{remark}, disable_remark = #{disableRemark}, status = #{status}, handle_status = #{handleStatus}, updater_account = #{updaterAccount}, update_date = #{updateDate}, confirmer_account = #{confirmerAccount}, confirm_date = #{confirmDate}, data_source_sys = #{dataSourceSys},
where process_step_sid = #{processStepSid}

