mapper.select:
select ifnull(sum(t.settle_quantity), 0) as settle_quantity from s_pay_product_jijian_settle_infor t


        and t.plant_sid = #{plantSid}


        and t.work_center_sid = #{workCenterSid}


        and t.product_price_type = #{productPriceType}


        and t.jixin_wangong_type = #{jixinWangongType}


        and t.department = #{department}


        and t.yearmonth = #{yearmonth}


        and t.product_sid = #{productSid}


        and t.paichan_batch = #{paichanBatch}


        and t.paichan_batch is null


        and t.handle_status = #{handleStatus}

SELECT t0.plant_sid,t0.product_sid,t0.paichan_batch,t0.paichan_batch as paichan_batch_to_string,t0.department,t0.product_price_type,t0.jixin_wangong_type, IFNULL(x.settle_quantity_check,0) as settle_quantity_check, IFNULL(y.settle_quantity_total,0) as settle_quantity_total,t1.plant_code,t1.plant_name,t1.short_name as plant_short_name,t2.material_code as product_code,t2.material_name, t3.customer_sid,t3.customer_code,t3.customer_name,t3.short_name as customer_short_name,t11.name as department_name,t12.shicai_quantity, ifnull((t12.shicai_quantity - ifnull(x.settle_quantity_check, 0)), 0) as wei_settle_quantity_check, ifnull((t12.shicai_quantity - ifnull(y.settle_quantity_total, 0)), 0) as wei_settle_quantity_total, t13.total_price_blq, t13.total_price_blh FROM ( SELECT t.plant_sid,t.product_sid,t.paichan_batch,t.department,t.product_price_type,t.jixin_wangong_type FROM s_pay_product_jijian_settle_infor t GROUP BY t.plant_sid,t.product_sid,t.paichan_batch,t.department,t.product_price_type,t.jixin_wangong_type ) t0 LEFT JOIN ( SELECT t.plant_sid,t.product_sid,t.paichan_batch,t.department,t.product_price_type,t.jixin_wangong_type,SUM(t.settle_quantity) as settle_quantity_check FROM s_pay_product_jijian_settle_infor t WHERE t.handle_status = '5' GROUP BY t.plant_sid,t.product_sid,t.paichan_batch,t.department,t.product_price_type,t.jixin_wangong_type ) x ON t0.plant_sid = x.plant_sid and t0.product_sid = x.product_sid and t0.department = x.department and t0.product_price_type = x.product_price_type and t0.jixin_wangong_type = x.jixin_wangong_type and (t0.paichan_batch = x.paichan_batch or (t0.paichan_batch is null and x.paichan_batch is null)) LEFT JOIN ( SELECT t.plant_sid,t.product_sid,t.paichan_batch,t.department,t.product_price_type,t.jixin_wangong_type,SUM(t.settle_quantity) as settle_quantity_total FROM s_pay_product_jijian_settle_infor t WHERE t.handle_status in ('5','1') GROUP BY t.plant_sid,t.product_sid,t.paichan_batch,t.department,t.product_price_type,t.jixin_wangong_type ) y ON t0.plant_sid = y.plant_sid and t0.product_sid = y.product_sid and t0.department = y.department and t0.product_price_type = y.product_price_type and t0.jixin_wangong_type = y.jixin_wangong_type and (t0.paichan_batch = y.paichan_batch or (t0.paichan_batch is null and y.paichan_batch is null)) LEFT JOIN s_bas_plant t1 ON t0.plant_sid = t1.plant_sid LEFT JOIN s_bas_material t2 ON t0.product_sid = t2.material_sid LEFT JOIN s_bas_customer t3 ON t2.customer_sid = t3.customer_sid LEFT JOIN sys_dict_data t9 ON t9.dict_type = 's_product_price_type' AND t0.product_price_type = t9.dict_value LEFT JOIN sys_dict_data t10 ON t10.dict_type = 's_jixin_wangong_type' AND t0.jixin_wangong_type = t10.dict_value LEFT JOIN s_con_manufacture_department t11 ON t0.department = t11.code LEFT JOIN s_man_product_produce_batch_infor t12 ON t0.plant_sid = t12.plant_sid and t0.product_sid = t12.product_sid and (t0.paichan_batch = t12.paichan_batch or (t0.paichan_batch is null and t12.paichan_batch is null)) LEFT JOIN s_pay_product_process_step t13 ON t0.plant_sid = t13.plant_sid and t0.product_sid = t13.product_sid and t0.department = t13.department and t0.product_price_type = t13.product_price_type and t0.jixin_wangong_type = t13.jixin_wangong_type
and t0.plant_sid = #{plantSid}
and t0.plant_sid in (#{item})
and t0.product_price_type = #{productPriceType}
and t0.jixin_wangong_type = #{jixinWangongType}
and t0.department = #{department}
and t2.material_name like concat('%', #{materialName}, '%')
and t2.material_code like concat('%', #{productCode}, '%')
and t0.product_sid = #{productSid}
and t0.paichan_batch = #{paichanBatchToString}
and t0.paichan_batch is null
and t12.shicai_quantity is not null
and t12.shicai_quantity is null
order by CONVERT(t1.short_name USING gbk) ASC, CONVERT(t2.material_code USING gbk) ASC, t0.paichan_batch ASC, CONVERT(t0.department USING gbk) ASC, CONVERT(t9.dict_label USING gbk) ASC, CONVERT(t10.dict_label USING gbk) ASC LIMIT ${pageBegin},${pageSize}




