mapper.select:
SELECT
    t2.client_id, t2.step_item_sid, t2.product_process_step_sid, t2.step_category, t2.process_step_code, t2.process_step_name, t2.process_step_sid,
       t2.price, t2.price_rate, t2.standard_price, t2.process_sid, t2.is_final, t2.task_unit, t2.sort, t2.remark, t2.creator_account, t2.create_date,
       t2.updater_account, t2.update_date, t2.data_source_sys, t3.product_sid, t3.handle_status, t3.plant_sid,
       t1.manufacture_order_code, t1.manufacture_order_sid, t1.plant_sid AS order_plant_sid, t1.material_sid,
       t4.plant_name, t4.short_name, t4.short_name as plant_short_name, t5.material_code AS product_code, t5.material_name, t6.process_name,
       t7.name as department_name
FROM s_man_manufacture_order_process t
 LEFT JOIN s_man_manufacture_order t1 ON t1.manufacture_order_sid = t.manufacture_order_sid AND t1.handle_status = '5'
 LEFT JOIN s_pay_product_process_step_item t2 ON t.process_sid = t2.process_sid
 LEFT JOIN s_pay_product_process_step t3 ON t3.handle_status = '5' AND t2.product_process_step_sid = t3.product_process_step_sid AND t.plant_sid = t3.plant_sid AND t1.material_sid = t3.product_sid
 LEFT JOIN s_bas_plant t4 ON t.plant_sid = t4.plant_sid
 LEFT JOIN s_bas_material t5 ON t3.product_sid = t5.material_sid
 LEFT JOIN s_man_process t6 ON t6.process_sid = t2.process_sid
LEFT JOIN s_con_manufacture_department t7 ON t3.department = t7.code


        and t.client_id = #{clientId}


        and t.plant_sid = #{plantSid}


        and t.work_center_sid = #{workCenterSid}


        and t1.manufacture_order_code like concat('%', #{manufactureOrderCode}, '%')


        and t2.process_step_name like concat('%', #{processStepName}, '%')


        and t2.process_step_sid = #{processStepSid}


        and t2.sort = #{sort}


        and t3.product_sid = #{productSid}


        and t3.product_price_type = #{productPriceType}


        and t3.jixin_wangong_type = #{jixinWangongType}


        and t3.department = #{department}


        and t5.material_code like concat('%', #{materialCode}, '%')


GROUP BY t1.manufacture_order_sid, t1.material_sid, t2.process_step_sid
ORDER BY CONVERT (t5.material_code USING gbk) ASC, CONVERT (t7.name USING gbk) ASC, t2.sort ASC

SELECT t2.client_id, t2.step_item_sid, t2.product_process_step_sid, t2.step_category, t2.process_step_code, t2.process_step_name,
       t2.process_step_sid, t2.price, t2.price_rate, t2.standard_price, t2.process_sid, t2.is_final, t2.task_unit, t2.sort, t2.remark,
       t2.creator_account, t2.create_date, t2.updater_account, t2.update_date, t2.data_source_sys, t3.product_sid, t3.handle_status,
       t4.plant_name, t4.short_name, t5.material_code AS product_code, t5.material_name, t6.process_name
FROM s_pay_product_process_step_item t2
         LEFT JOIN s_pay_product_process_step t3 ON t3.handle_status = '5' AND t2.product_process_step_sid = t3.product_process_step_sid
         LEFT JOIN s_bas_plant t4 ON t3.plant_sid = t4.plant_sid
         LEFT JOIN s_bas_material t5 ON t3.product_sid = t5.material_sid
         LEFT JOIN s_man_process t6 ON t6.process_sid = t2.process_sid


        and t3.plant_sid = #{plantSid}


        and t2.process_step_name like concat('%', #{processStepName}, '%')


        and t2.process_step_sid = #{processStepSid}


        and t3.product_sid = #{productSid}


        and t3.product_price_type = #{productPriceType}


        and t3.jixin_wangong_type = #{jixinWangongType}


        and t5.material_code like concat('%', #{materialCode}, '%')

mapper.sql:
select t.client_id, t.step_item_sid, t.product_process_step_sid, t.step_category, t.process_step_sid, t.process_step_code, t.process_step_name, t.price, t.price_rate, (t.price * t.price_rate) as actual_price, t.standard_price, t.process_sid, t.is_final, t.task_unit, t.sort, t.remark, t.creator_account, t.create_date, t1.product_sid, t1.product_code, t1.department, t1.plant_sid, t1.plant_code, t1.product_price_type, t1.jixin_wangong_type, t1.limit_price, t1.product_price_rate, t1.currency, t1.currency_unit, t1.handle_status, t2.process_code, t2.process_name, t3.material_name, t3.sample_code_self, t3.simple_code, t4.plant_name, t4.short_name as plant_short_name, t7.price_total, t8.name as task_unit_name, t9.nick_name as creator_account_name, t10.name as department_name from s_pay_product_process_step_item t LEFT JOIN s_pay_product_process_step t1 ON t.product_process_step_sid = t1.product_process_step_sid LEFT JOIN s_man_process t2 ON t.process_sid = t2.process_sid LEFT JOIN s_bas_material t3 ON t1.product_sid = t3.material_sid LEFT JOIN s_bas_plant t4 ON t1.plant_sid = t4.plant_sid LEFT JOIN sys_dict_data t5 ON t5.dict_type = 's_product_price_type' AND t1.product_price_type = t5.dict_value LEFT JOIN sys_dict_data t6 ON t6.dict_type = 's_jixin_wangong_type' AND t1.jixin_wangong_type = t6.dict_value LEFT JOIN (SELECT product_process_step_sid,ROUND(SUM(price * price_rate),3) AS price_total FROM s_pay_product_process_step_item GROUP BY product_process_step_sid) t7 ON t.product_process_step_sid = t7.product_process_step_sid LEFT JOIN s_con_measure_unit t8 ON t.task_unit = t8.code LEFT JOIN sys_user t9 ON t.creator_account = t9.user_name AND t.client_id = t9.client_id LEFT JOIN s_con_manufacture_department t10 ON t1.department = t10.code
and t.client_id = #{clientId}
and t.step_item_sid = #{stepItemSid}
and t.product_process_step_sid = #{productProcessStepSid}
and t.step_category = #{stepCategory}
and t.process_step_sid = #{processStepSid}
and t.process_step_code like concat('%', #{processStepCode}, '%')
and t.process_step_name like concat('%', #{processStepName}, '%')
and t.process_sid = #{processSid}
and t.process_sid in (#{item})
and t.creator_account = #{creatorAccount}
and t.sort = #{sort}
and t.creator_account in (#{item})
and t1.product_sid = #{productSid}
and t1.product_code like concat('%', #{productCode}, '%')
and t1.department = #{department}
and t1.department in (#{item})
and t1.plant_sid =#{plantSid}
and t1.plant_sid in (#{item})
and t1.product_price_type = #{productPriceType}
and t1.product_price_type in (#{item})
and t1.jixin_wangong_type = #{jixinWangongType}
and t1.jixin_wangong_type in (#{item})
and t1.handle_status = #{handleStatus}
and t1.handle_status in (#{item})
and t3.material_name like concat('%', #{materialName}, '%')
and t3.sample_code_self like concat('%', #{sampleCodeSelf}, '%')
and date_format(t.create_date,'%y%m%d') >= date_format(#{beginTime},'%y%m%d')
and date_format(t.create_date,'%y%m%d') <= date_format(#{endTime},'%y%m%d')

mapper.select:
select t.client_id, t.step_item_sid, t.step_item_sid as process_step_item_sid, t.product_process_step_sid, t.step_category, t.process_step_sid, t.process_step_code, t.process_step_name, t.price, t.price_rate, t.process_sid, t.sort, t1.department, t2.process_code, t2.process_name, t3.name as department_name, t4.material_name, t4.material_name as product_name from s_pay_product_process_step_item t LEFT JOIN s_pay_product_process_step t1 ON t.product_process_step_sid = t1.product_process_step_sid LEFT JOIN s_man_process t2 ON t.process_sid = t2.process_sid LEFT JOIN s_con_manufacture_department t3 ON t1.department = t3.code LEFT JOIN s_bas_material t4 ON t1.product_sid = t4.material_sid
and t.client_id = #{clientId}
and t.step_item_sid = #{stepItemSid}
and t.product_process_step_sid = #{productProcessStepSid}
and t.step_category = #{stepCategory}
and t.process_step_sid = #{processStepSid}
and t.process_sid = #{processSid}
and t.process_sid in (#{item})
and t.sort = #{sort}
and t1.product_sid = #{productSid}
and t1.product_code like concat('%', #{productCode}, '%')
and t1.department = #{department}
and t1.department in (#{item})
and t1.plant_sid =#{plantSid}
and t1.plant_sid in (#{item})
and t1.product_price_type = #{productPriceType}
and t1.product_price_type in (#{item})
and t1.jixin_wangong_type = #{jixinWangongType}
and t1.jixin_wangong_type in (#{item})
and t1.handle_status = #{handleStatus}
and t1.handle_status in (#{item})
order by t.sort asc
