mapper.select:



        and (t.client_id like concat('%',
        #{item}, '%')
    )


        and t.product_process_step_sid =#{productProcessStepSid}


        and t.department =#{department}


        and t.department in
        (#{department})


        and t.product_price_type = #{productPriceType}


        and t.product_price_type in
        (#{productPriceType})


        and t.jixin_wangong_type = #{jixinWangongType}


        and t.jixin_wangong_type in
        (#{jixinWangongType})


        and t.plant_sid =#{plantSid}


        and t.plant_sid in
        (#{item})


        and (t.plant_code
        like concat('%',
        #{item}, '%')
    )


        and t.product_process_step_sid in
        (#{item})


        and t.product_sid =#{productSid}


        and (t1.sample_code_self like
        concat('%',
        #{item}, '%')
    )


        and (t.product_code like
        concat('%',
        #{item}, '%')
    )


        and (t1.material_name like
        concat('%',
        #{item}, '%')
    )


        and (t1.simple_code like
        concat('%',
        #{item}, '%')
    )


        and t.limit_price = #{limitPrice}


        and t.product_price_rate = #{productPriceRate}


        and (t.currency like concat('%',
        #{item}, '%')
    )


        and (t.currency_unit like
        concat('%',
        #{item}, '%')
    )


        and (t.handle_status like
        concat('%',
        #{item}, '%')
    )


        and t.handle_status in
        (#{item})


        and (t2.nick_name like
        concat('%',
        #{item}, '%')
    )


        and date_format(t.create_date,'%y%m%d') >= date_format(#{beginTime},'%y%m%d')


        and date_format(t.create_date,'%y%m%d') <= date_format(#{endTime},'%y%m%d')


        and (t.updater_account like
        concat('%',
        #{item}, '%')
    )


        and t.update_date = #{updateDate}


        and (t.confirmer_account
        like concat('%',
        #{item}, '%')
    )


        and t.confirm_date = #{confirmDate}


        and (t.data_source_sys like
        concat('%',
        #{item}, '%')
    )


        ${params.dataScope}


order by t.create_date desc

