client_id = #{clientId}, product_sid = #{productSid}, product_process_step_sid = #{productProcessStepSid}, product_code = #{productCode}, department = #{department}, limit_price = #{limitPrice}, product_price_rate = #{productPriceRate} total_price_blh = #{totalPriceBlh}, total_price_blq = #{totalPriceBlq}, currency = #{currency}, currency_unit = #{currencyUnit}, remark = #{remark}, handle_status = #{handleStatus}, creator_account = #{creatorAccount}, create_date = #{createDate}, updater_account = #{updaterAccount}, update_date = #{updateDate}, confirmer_account = #{confirmerAccount}, confirm_date = #{confirmDate}, data_source_sys = #{dataSourceSys}, plant_sid = #{plantSid}, plant_code = #{plantCode}, product_price_type = #{productPriceType}, jixin_wangong_type = #{jixinWangongType},
where product_process_step_sid = #{productProcessStepSid}

mapper.select:
select product_process_step_sid, product_sid, product_code, department, handle_status, creator_account, create_date from s_pay_product_process_step
handle_status = #{handleStatus}
department = #{department}
order by product_code
