select.where:
and t.del_flag_biangz = 'Y'

and t.client_id = #{clientId}


and t.update_product_process_step_sid = #{updateProductProcessStepSid}


and t.step_item_sid = #{stepItemSid}

ifnull(t.del_flag_biangz,'') = ''

    and t.client_id = #{clientId}


    and t.update_product_process_step_sid = #{updateProductProcessStepSid}


    and t.step_item_sid = #{stepItemSid}


    and t.step_category = #{stepCategory}


    and t.process_step_code = #{processStepCode}

and
    t.process_step_name like concat('%', #{processStepName}, '%')


    and t.process_step_sid = #{processStepSid}


    and t.price = #{price}


    and t.price_rate = #{priceRate}


    and t.standard_price = #{standardPrice}


    and t.process_sid = #{processSid}


    and t.is_final = #{isFinal}


    and t.task_unit = #{taskUnit}


    and t.sort = #{sort}


    and t.creator_account = #{creatorAccount}


    and date_format(t.create_date,'%y%m%d') >= date_format(#{beginTime},'%y%m%d')


    and date_format(t.create_date,'%y%m%d') <= date_format(#{endTime},'%y%m%d')


    and t.updater_account = #{updaterAccount}


    and t.update_date = #{updateDate}


    and t.data_source_sys = #{dataSourceSys}


    and t.del_flag_biangz = #{delFlagBiangz}


    ${params.dataScope}

mapper.update:
update s_pay_update_product_process_step_item
step_category = #{stepCategory}, process_step_code = #{processStepCode}, process_step_name = #{processStepName}, process_step_sid = #{processStepSid}, price = #{price}, price_rate = #{priceRate}, price_bgq = #{priceBgq}, price_rate_bgq = #{priceRateBgq}, standard_price = #{standardPrice}, process_sid = #{processSid}, is_final = #{isFinal}, task_unit = #{taskUnit}, sort = #{sort}, del_flag_biangz = #{delFlagBiangz}, remark = #{remark}, updater_account = #{updaterAccount}, update_date = #{updateDate},
where update_step_item_sid = #{updateStepItemSid}

update.foreach:
update s_pay_update_product_process_step_item
step_category = #{stepCategory}, process_step_code = #{processStepCode}, process_step_name = #{processStepName}, process_step_sid = #{processStepSid}, price = #{price}, price_rate = #{priceRate}, price_bgq = #{priceBgq}, price_rate_bgq = #{priceRateBgq}, standard_price = #{standardPrice}, process_sid = #{processSid}, is_final = #{isFinal}, task_unit = #{taskUnit}, sort = #{sort}, remark = #{remark}, updater_account = #{updaterAccount}, update_date = #{updateDate},
where update_step_item_sid = #{updateStepItemSid}
