mapper.select:



        and t.client_id = #{clientId}


        and t.product_process_step_sid = #{productProcessStepSid}


        and t.product_process_step_sid in
        (#{productProcessStepSid})


        and t.product_sid = #{productSid}


        and t.product_code like concat('%' , #{productCode} , '%')


        and (t5.material_name like
        concat('%',
        #{item}, '%')
    )


        and t.handle_status in
        (#{item})


        and (t2.nick_name like
        concat('%',
        #{item}, '%')
    )


        and t.plant_sid = #{plantSid}


        and t.plant_sid in
        (#{plantSid})


        and t.plant_code like concat('%' , #{plantCode} , '%')


        and t.product_price_type = #{productPriceType}


        and t.product_price_type in
        (#{productPriceType})


        and t.jixin_wangong_type = #{jixinWangongType}


        and t.jixin_wangong_type in
        (#{jixinWangongType})


        and t.department = #{department}


        and t.department in
        (#{department})


        and t.limit_price = #{limitPrice}


        and t.product_price_rate = #{productPriceRate}


        and t.total_price_blh = #{totalPriceBlh}


        and t.total_price_blq = #{totalPriceBlq}


        and t.currency = #{currency}


        and t.currency_unit = #{currencyUnit}


        and t.update_remark = #{updateRemark}


        and t.update_version_id = #{updateVersionId}


        and t.handle_status = #{handleStatus}


        and date_format(t.create_date,'%y%m%d') >= date_format(#{beginTime},'%y%m%d')


        and date_format(t.create_date,'%y%m%d') <= date_format(#{endTime},'%y%m%d')


        and t.updater_account = #{updaterAccount}


        and t.update_date = #{updateDate}


        and t.confirmer_account = #{confirmerAccount}


        and t.confirm_date = #{confirmDate}


        and t.data_source_sys = #{dataSourceSys}


        and t5.sample_code_self like concat('%', #{sampleCodeSelf}, '%')


        ${params.dataScope}


order by t.create_date desc

