mapper.select:



        and t.client_id = #{clientId}


        and t.task_template_sid = #{taskTemplateSid}


        and t.task_template_sid in
        (
        #{taskTemplateSid})


        and t.task_template_name like concat('%', #{taskTemplateName}, '%')


        and t.task_template_code = #{taskTemplateCode}


        and t.project_type = #{projectType}


        and t.project_type in
        (#{projectType})


        and t.template_time = #{templateTime}


        and t.status = #{status}


        and t.handle_status = #{handleStatus}


        and t.handle_status in
        (#{handleStatus})


        and t.creator_account = #{creatorAccount}


        and t2.nick_name like concat('%', #{creatorAccountName}, '%')


        and date_format(t.create_date,'%y%m%d') = date_format(#{createDate},'%y%m%d')


        and date_format(t.create_date,'%y%m%d') >= date_format(#{beginTime},'%y%m%d')


        and date_format(t.create_date,'%y%m%d') <= date_format(#{endTime},'%y%m%d')


        and t.updater_account = #{updaterAccount}


        and t.update_date = #{updateDate}


        and t.confirmer_account = #{confirmerAccount}


        and t.confirm_date = #{confirmDate}


        and t.data_source_sys = #{dataSourceSys}


        ${params.dataScope}


order by t.task_template_code desc

mapper.update:
update s_prj_task_template
client_id = #{clientId}, task_template_name = #{taskTemplateName}, task_template_code = #{taskTemplateCode}, project_type = #{projectType}, template_time = #{templateTime}, remark = #{remark}, status = #{status}, handle_status = #{handleStatus}, updater_account = #{updaterAccount}, update_date = #{updateDate}, confirmer_account = #{confirmerAccount}, confirm_date = #{confirmDate},
where task_template_sid = #{taskTemplateSid}

update.foreach:
update s_prj_task_template
client_id = #{clientId}, task_template_name = #{taskTemplateName}, task_template_code = #{taskTemplateCode}, project_type = #{projectType}, template_time = #{templateTime}, remark = #{remark}, status = #{status}, handle_status = #{handleStatus}, updater_account = #{updaterAccount}, update_date = #{updateDate}, confirmer_account = #{confirmerAccount}, confirm_date = #{confirmDate},
where task_template_sid = #{taskTemplateSid}
