mapper.select:



        and (t2.material_code like concat('%',
        #{item}, '%')
    )


        and (t2.material_name like concat('%',
        #{item}, '%')
    )


        and ( t.outsource_purchase_price_code like concat('%',
        #{item}, '%')
          )


        and ( t.vendor_sid like concat('%',
        #{item}, '%')
    )


        and t.material_sid =#{materialSid}


        and t.material_category =#{materialCategory}


        and t.sku1_sid =#{sku1Sid}


        and t.sku2_sid =#{sku2Sid}


        and t.barcode_sid =#{barcodeSid}


        and ( t.process_sid like concat('%',
        #{item}, '%')
    )


        and ( t.company_sid like concat('%',
        #{item}, '%')
    )


        and (t.purchase_org like
        concat('%',
        #{item}, '%')
    )


        and (t.price_dimension like
        concat('%',
        #{item}, '%')
    )


        and (t.status like concat('%',
        #{item}, '%')
    )


        and ( t.handle_status like concat('%',
        #{item}, '%')
    )


        and (t8.nick_name like
        concat('%',
        #{item}, '%')
    )


        and date_format(t.create_date,'%y%m%d') >= date_format(#{beginTime},'%y%m%d')


        and date_format(t.create_date,'%y%m%d') <= date_format(#{endTime},'%y%m%d')


        and (t.updater_account like
        concat('%',
        #{item}, '%')
    )


        and t.update_date = #{updateDate}


        and (t.confirmer_account
        like concat('%',
        #{item}, '%')
    )


        and t.confirm_date = #{confirmDate}


        and (t.data_source_sys like
        concat('%',
        #{item}, '%')
    )


        ${params.dataScope}


order by t.outsource_purchase_price_code desc

