mapper.update:
update s_pur_outsource_quote_bargain_item
client_id = #{clientId}, material_sid = #{materialSid}, sku1_sid = #{sku1Sid}, sku2_sid = #{sku2Sid}, barcode_sid = #{barcodeSid}, process_sid = #{processSid}, labor_type_item_sid = #{laborTypeItemSid}, customer_price_tax = #{customerPriceTax}, customer_price = #{customerPrice}, price_dimension = #{priceDimension}, cascade_type = #{cascadeType}, price_enter_mode = #{priceEnterMode}, quote_price_tax = #{quotePriceTax}, quote_price = #{quotePrice}, check_price_tax = #{checkPriceTax}, check_price = #{checkPrice}, confirm_price_tax = #{confirmPriceTax}, confirm_price = #{confirmPrice}, purchase_price_tax = #{purchasePriceTax}, purchase_price = #{purchasePrice}, tax_rate = #{taxRate}, currency = #{currency}, currency_unit = #{currencyUnit}, unit_base = #{unitBase}, unit_price = #{unitPrice}, unit_conversion_rate = #{unitConversionRate}, quote_updater_account = #{quoteUpdaterAccount}, quote_update_date = #{quoteUpdateDate}, check_updater_account = #{checkUpdaterAccount}, check_update_date = #{checkUpdateDate}, confirm_updater_account = #{confirmUpdaterAccount}, confirm_update_date = #{confirmUpdateDate}, purchase_updater_account = #{purchaseUpdaterAccount}, purchase_update_date = #{purchaseUpdateDate}, current_stage = #{currentStage}, remark_request = #{remarkRequest}, remark_quote = #{remarkQuote}, remark_check = #{remarkCheck}, remark_confirm = #{remarkConfirm}, remark_purchase = #{remarkPurchase}, remark = #{remark}, creator_account = #{creatorAccount}, create_date = #{createDate}, updater_account = #{updaterAccount}, update_date = #{updateDate}, confirmer_account = #{confirmerAccount}, confirm_date = #{confirmDate}, data_source_sys = #{dataSourceSys},
where outsource_quote_bargain_item_sid = #{outsourceQuoteBargainItemSid}

update.foreach:
update s_pur_outsource_quote_bargain_item
client_id = #{clientId}, outsource_request_quotation_sid = #{outsourceRequestQuotationSid}, material_sid = #{materialSid}, sku1_sid = #{sku1Sid}, sku2_sid = #{sku2Sid}, barcode_sid = #{barcodeSid}, labor_type_item_sid = #{laborTypeItemSid}, price_dimension = #{priceDimension}, cascade_type = #{cascadeType}, price_enter_mode = #{priceEnterMode}, quote_price_tax = #{quotePriceTax}, quote_price = #{quotePrice}, check_price_tax = #{checkPriceTax}, check_price = #{checkPrice}, confirm_price_tax = #{confirmPriceTax}, confirm_price = #{confirmPrice}, purchase_price_tax = #{purchasePriceTax}, purchase_price = #{purchasePrice}, tax_rate = #{taxRate}, currency = #{currency}, currency_unit = #{currencyUnit}, unit_base = #{unitBase}, unit_price = #{unitPrice}, unit_conversion_rate = #{unitConversionRate}, quote_updater_account = #{quoteUpdaterAccount}, quote_update_date = #{quoteUpdateDate}, check_updater_account = #{checkUpdaterAccount}, check_update_date = #{checkUpdateDate}, confirm_updater_account = #{confirmUpdaterAccount}, confirm_update_date = #{confirmUpdateDate}, purchase_updater_account = #{purchaseUpdaterAccount}, purchase_update_date = #{purchaseUpdateDate}, current_stage = #{currentStage}, remark_request = #{remarkRequest}, remark_quote = #{remarkQuote}, remark_check = #{remarkCheck}, remark_confirm = #{remarkConfirm}, remark_purchase = #{remarkPurchase}, remark = #{remark}, creator_account = #{creatorAccount}, create_date = #{createDate}, updater_account = #{updaterAccount}, update_date = #{updateDate}, confirmer_account = #{confirmerAccount}, confirm_date = #{confirmDate}, data_source_sys = #{dataSourceSys},
where outsource_request_quotation_item_sid = #{outsourceRequestQuotationItemSid}

mapper.select:
select t.*,t1.price_dimension,t1.outsource_purchase_price_code
from s_pur_outsource_purchase_price_item t
left join s_pur_outsource_purchase_price t1 on t.outsource_purchase_price_sid = t1.outsource_purchase_price_sid


        and t.handle_status in (#{handleStatus})


        and t1.vendor_sid = #{vendorSid}


        and t1.material_sid = #{materialSid}


        and t1.sku1_sid = #{sku1Sid}


        and t1.process_sid = #{processSid}


        and t1.price_dimension = #{priceDimension}


        and ((date_format(t.start_date,'%y%m%d') < date_format(#{startDate},'%y%m%d') and date_format(t.end_date,'%y%m%d') > date_format(#{startDate},'%y%m%d'))

            or (date_format(t.start_date,'%y%m%d') > date_format(#{startDate},'%y%m%d') and date_format(t.start_date,'%y%m%d') <= date_format(#{endDate},'%y%m%d'))
            or (date_format(t.end_date,'%y%m%d') >= date_format(#{startDate},'%y%m%d') and date_format(t.end_date,'%y%m%d') <= date_format(#{endDate},'%y%m%d'))
        )
