        and purchase_contract_sid in (

            #{item}

        )

    and handle_status in (1,4)

select t.purchase_contract_sid, t.purchase_contract_code, t.contract_name, t.vendor_sid, t.company_sid, t.close_status, t.handle_status, t.create_date,
       t1.vendor_code, t1.vendor_name, ifnull(t1.short_name, t1.vendor_name) as vendor_short_name,
       t2.company_code, t2.company_name, ifnull(t2.short_name, t2.company_name) as company_short_name
from s_pur_purchase_contract t
left join s_bas_vendor t1 on t.vendor_sid = t1.vendor_sid
left join s_bas_company t2 on t.company_sid = t2.company_sid


        and t.vendor_sid = #{vendorSid}


        and t.company_sid = #{companySid}


        and t.handle_status = #{handleStatus}


        and t.handle_status in
        (#{handleStatus})


        and t.close_status = #{closeStatus}


order by t.create_date desc

select purchase_contract_sid, purchase_contract_code, contract_name, handle_status, create_date from s_pur_purchase_contract
and company_sid = #{companySid}
and vendor_sid = #{vendorSid}
and handle_status = #{handleStatus}
order by contract_name

SELECT ifnull(t.num_contract, 0) as num_contract, ifnull(t.sum_amount_tax_contract, 0) as sum_amount_tax_contract, t.year,t.vendor_sid,ifnull(t.contract_type,'') as contract_type, ifnull(t1.num_order, 0) as num_order, t2.vendor_name, ifnull(t2.short_name,t2.vendor_name) as vendor_short_name FROM ( SELECT COUNT(x.purchase_contract_sid) as num_contract, SUM(x.currency_amount_tax) as sum_amount_tax_contract, x.year, x.vendor_sid, x.contract_type FROM s_pur_purchase_contract x WHERE x.handle_status IN ('5', '9') GROUP BY x.vendor_sid,x.year,x.contract_type ) t LEFT JOIN ( SELECT COUNT(distinct y.purchase_order_sid) as num_order, y2.year, y2.contract_type, y.vendor_sid FROM s_pur_purchase_order y LEFT JOIN s_pur_purchase_contract y2 ON y.purchase_contract_sid = y2.purchase_contract_sid WHERE y.handle_status IN ('5', '7') and y2.handle_status IN ('5', '9') GROUP BY y2.vendor_sid, y2.year, y2.contract_type ) t1 ON t.year = t1.year AND t.contract_type = t1.contract_type AND t.vendor_sid = t1.vendor_sid LEFT JOIN s_bas_vendor t2 ON t.vendor_sid = t2.vendor_sid
and t.vendor_sid in (#{vendorSid})
and t.company_sid in (#{companySid})
and t.year in (#{year})
and t.contract_type in (#{contractType})
and t.year = #{year}
and t.vendor_sid = #{vendorSid}
and t.company_sid = #{companySid}
and t.contract_type = #{contractType}
ORDER BY t.year DESC, t2.vendor_name ASC, t.contract_type ASC

SELECT ifnull(t.num_contract, 0) as num_contract, ifnull(t.sum_amount_tax_contract, 0) as sum_amount_tax_contract, t.year,t.vendor_sid,ifnull(t.contract_type, '') as contract_type, ifnull(t1.num_order, 0) as num_order, t.company_sid,t.product_season_sid,t.purchase_mode,t.raw_material_mode, t2.vendor_name,ifnull(t2.short_name, t2.vendor_name) as vendor_short_name ,t3.company_name, ifnull(t3.short_name, t3.company_name) as company_short_name, ifnull(t4.product_season_name, '') as product_season_name FROM ( SELECT COUNT(x.purchase_contract_sid) as num_contract, SUM(x.currency_amount_tax) as sum_amount_tax_contract, x.year, x.vendor_sid, x.contract_type, x.company_sid,x.product_season_sid,x.purchase_mode,x.raw_material_mode FROM s_pur_purchase_contract x
x.handle_status IN ('5','9')
and x.vendor_sid in (#{vendorSid})
and x.company_sid in (#{companySid})
and x.year in (#{year})
and x.contract_type in (#{contractType})
and x.year = #{year}
and x.vendor_sid = #{vendorSid}
and x.company_sid = #{companySid}
and x.contract_type = #{contractType}
GROUP BY x.company_sid,x.product_season_sid,x.purchase_mode,x.raw_material_mode ) t LEFT JOIN ( SELECT COUNT(distinct y.purchase_order_sid) as num_order, y2.year, y2.contract_type, y2.vendor_sid, y2.company_sid,y2.product_season_sid,y2.purchase_mode,y2.raw_material_mode FROM s_pur_purchase_order y LEFT JOIN s_pur_purchase_contract y2 ON y.purchase_contract_sid = y2.purchase_contract_sid
y2.handle_status IN ('5', '9') AND y.handle_status IN ('5','7')
and y2.vendor_sid in (#{vendorSid})
and y2.company_sid in (#{companySid})
and y2.year in (#{year})
and y2.contract_type in (#{contractType})
and y2.year = #{year}
and y2.vendor_sid = #{vendorSid}
and y2.company_sid = #{companySid}
and y2.contract_type = #{contractType}
GROUP BY y2.company_sid,y2.product_season_sid,y2.purchase_mode,y2.raw_material_mode ) t1 ON t.company_sid = t1.company_sid AND ((t.product_season_sid = t1.product_season_sid)or (t.product_season_sid is null and t1.product_season_sid is null )) AND ((t.purchase_mode = t1.purchase_mode)or (t.purchase_mode is null and t1.purchase_mode is null )) AND ((t.raw_material_mode = t1.raw_material_mode)or(t.raw_material_mode is null and t1.raw_material_mode is null)) LEFT JOIN s_bas_vendor t2 ON t.vendor_sid = t2.vendor_sid LEFT JOIN s_bas_company t3 ON t.company_sid = t3.company_sid LEFT JOIN s_bas_product_season t4 ON t.product_season_sid = t4.product_season_sid ORDER BY t3.short_name ASC, t4.product_season_name ASC, t.purchase_mode ASC, t.raw_material_mode ASC

select t.*, t1.nick_name as creator_account_name, t2.name as file_type_name, t3.name as object_name
from s_contract_template_attach t
left join sys_user t1 on t.creator_account = t1.user_name and t.client_id = t1.client_id
left join s_con_file_type t2 on t.file_type = t2.code
left join s_con_dataobject_category t3 on t.object_category = t3.code


        and t.object_category = #{objectCategory}


        and t.file_type = #{fileType}


        and t.creator_account = #{creatorAccount}

SELECT t.client_id, t.purchase_contract_sid, t.purchase_contract_code, t.buyer, t2.user_id as buyer_id, t2.nick_name as buyer_name, t3.purchase_finance_account_id FROM s_pur_purchase_contract t LEFT JOIN sys_user t2 ON t.buyer = t2.user_name AND t.client_id = t2.client_id LEFT JOIN ( SELECT ta.client_id, GROUP_CONCAT(tb.user_id ORDER BY tb.user_id ASC SEPARATOR ';') as purchase_finance_account_id FROM s_sys_default_setting_client ta LEFT JOIN sys_user tb ON ta.purchase_finance_account like concat('%;', tb.user_name, ';%') or ta.purchase_finance_account like concat(tb.user_name, ';%') or ta.purchase_finance_account like concat('%;', tb.user_name) GROUP BY ta.client_id ) t3 ON t.client_id = t3.client_id
and t.client_id = #{clientId}
and t.handle_status = #{handleStatus}
and t.sign_in_status = #{signInStatus}
and t.upload_status = #{uploadStatus}
and t.contract_type = #{contractType}
