SELECT t.client_id, t.purchase_order_sid, t.purchase_order_code, t.purchase_contract_sid, t.purchase_contract_code, t.buyer, t2.user_id as buyer_id, t2.nick_name as buyer_name FROM s_pur_purchase_order t LEFT JOIN s_pur_purchase_contract t1 ON t.purchase_contract_sid = t1.purchase_contract_sid LEFT JOIN sys_user t2 ON t.buyer = t2.user_name AND t.client_id = t2.client_id
and t.client_id = #{clientId}
and t.handle_status = #{handleStatus}
and t1.handle_status = #{contractHandleStatus}
and t1.contract_purpose = #{contractPurpose}

SELECT t.client_id, t.purchase_order_sid, t.purchase_order_code, t.purchase_contract_sid, t.purchase_contract_code, t.buyer, t2.user_id as buyer_id, t2.nick_name as buyer_name, t3.purchase_finance_account_id FROM s_pur_purchase_order t LEFT JOIN sys_user t2 ON t.buyer = t2.user_name AND t.client_id = t2.client_id LEFT JOIN ( SELECT ta.client_id, GROUP_CONCAT(tb.user_id ORDER BY tb.user_id ASC SEPARATOR ';') as purchase_finance_account_id FROM s_sys_default_setting_client ta LEFT JOIN sys_user tb ON ta.purchase_finance_account like concat('%;', tb.user_name, ';%') or ta.purchase_finance_account like concat(tb.user_name, ';%') or ta.purchase_finance_account like concat('%;', tb.user_name) GROUP BY ta.client_id ) t3 ON t.client_id = t3.client_id
and t.client_id = #{clientId}
and t.handle_status = #{handleStatus}
and t.sign_in_status = #{signInStatus}
and t.upload_status = #{uploadStatus}
