mapper.update:
update s_pur_purchase_price_item
start_date = #{startDate}, end_date = #{endDate}, cascade_type = #{cascadeType}, price_enter_mode = #{priceEnterMode}, is_recursion_price = #{isRecursionPrice}, unit_recursion = #{unitRecursion}, refer_quantity = #{referQuantity}, incre_quantity = #{increQuantity}, decre_quantity = #{decreQuantity}, price_min_quantity = #{priceMinQuantity}, rounding_type = #{roundingType}, incre_pur_price_tax = #{increPurPriceTax}, incre_pur_price = #{increPurPrice}, dec_pur_price_tax = #{decPurPriceTax}, dec_pur_price = #{decPurPrice}, contract_sid = #{contractSid}, purchase_price_tax = #{purchasePriceTax}, purchase_price = #{purchasePrice}, quote_price_tax = #{quotePriceTax}, check_price_tax = #{checkPriceTax}, price_remark = #{priceRemark}, tax_rate = #{taxRate}, currency = #{currency}, currency_unit = #{currencyUnit}, unit_price = #{unitPrice}, unit_base = #{unitBase}, unit_conversion_rate = #{unitConversionRate}, remark = #{remark}, update_remark = #{updateRemark}, updater_account = #{updaterAccount}, update_date = #{updateDate}, confirmer_account = #{confirmerAccount}, confirm_date = #{confirmDate}, sku_type_recursion = #{skuTypeRecursion}, handle_status = #{handleStatus}, item_num = #{itemNum},
where purchase_price_item_sid = #{purchasePriceItemSid}

