mapper.select:
select t.*,t1.price_dimension,t1.purchase_price_code,t1.purchase_price_sid
from s_pur_purchase_price_item t
left join s_pur_purchase_price t1 on t.purchase_price_sid = t1.purchase_price_sid


        and t.handle_status in (#{handleStatus})


        and t1.material_sid = #{materialSid}


        and t1.sku1_sid = #{sku1Sid}


        and t1.price_dimension = #{priceDimension}


        and t1.vendor_sid = #{vendorSid}


        and t1.raw_material_mode = #{rawMaterialMode}


        and t1.purchase_mode = #{purchaseMode}


        and ((date_format(t.start_date,'%y%m%d') < date_format(#{startDate},'%y%m%d') and date_format(t.end_date,'%y%m%d') > date_format(#{startDate},'%y%m%d'))

            or (date_format(t.start_date,'%y%m%d') > date_format(#{startDate},'%y%m%d') and date_format(t.start_date,'%y%m%d') <= date_format(#{endDate},'%y%m%d'))
            or (date_format(t.end_date,'%y%m%d') >= date_format(#{startDate},'%y%m%d') and date_format(t.end_date,'%y%m%d') <= date_format(#{endDate},'%y%m%d'))
        )

select t.*
from s_cos_product_cost t


        and t.handle_status in (#{handleStatus})


        and t.material_sid = #{materialSid}


        and t.sku1_sid = #{sku1Sid}


        and t.price_dimension = #{priceDimension}


        and t.vendor_sid = #{vendorSid}


        and t.raw_material_mode = #{rawMaterialMode}


        and t.business_mode = #{purchaseMode}
