SELECT t.company_sid, t.vendor_sid, t2.vendor_name, t2.vendor_code, t2.short_name AS vendor_short_name, t3.company_name, t3.company_code, ifnull(t3.short_name,t3.company_name) as company_short_name, t.document_type, CONCAT(t.company_sid,';',t.vendor_sid) AS one_key, SUM(t1.currency_amount - t1.currency_amount_yth) AS yajin, 0 AS yufukuan, 0 AS yingfuzangu, 0 AS yingfukuan, 0 AS koukuan, 0 AS tiaozhang, 0 AS teshufukuan, 0 AS zanyakuan FROM s_fin_vendor_cash_pledge_bill t LEFT JOIN s_fin_vendor_cash_pledge_bill_item t1 ON t1.cash_pledge_bill_sid = t.cash_pledge_bill_sid LEFT JOIN s_bas_vendor t2 ON t2.vendor_sid = t.vendor_sid LEFT JOIN s_bas_company t3 ON t3.company_sid = t.company_sid
and t1.return_status in (#{returnStatus})
and t.handle_status in (#{handleStatus})
and t.document_type in (#{documentType})
and t.document_type = #{documentType}
and t.vendor_sid = #{vendorSid}
and t.company_sid = #{companySid}
and t.vendor_sid in (#{vendorSid})
and t.company_sid in (#{companySid})
GROUP BY t.company_sid,t.vendor_sid,t.document_type

SELECT t.company_sid, t.vendor_sid, t2.vendor_name, t2.vendor_code, t2.short_name AS vendor_short_name, t3.company_name, t3.company_code, ifnull(t3.short_name,t3.company_name) as company_short_name, t.document_type, CONCAT(t.company_sid,';',t.vendor_sid) AS one_key, SUM(t1.currency_amount - t1.currency_amount_ysf) AS zanyakuan, 0 AS yufukuan, 0 AS yingfuzangu, 0 AS yingfukuan, 0 AS koukuan, 0 AS tiaozhang, 0 AS teshufukuan, 0 AS yajin FROM s_fin_vendor_funds_freeze_bill t LEFT JOIN s_fin_vendor_funds_freeze_bill_item t1 ON t1.funds_freeze_bill_sid = t.funds_freeze_bill_sid LEFT JOIN s_bas_vendor t2 ON t2.vendor_sid = t.vendor_sid LEFT JOIN s_bas_company t3 ON t3.company_sid = t.company_sid
and t1.unfreeze_status in (#{unfreezeStatus})
and t.handle_status in (#{handleStatus})
and t.document_type in (#{documentType})
and t.document_type = #{documentType}
and t.vendor_sid = #{vendorSid}
and t.company_sid = #{companySid}
and t.vendor_sid in (#{vendorSid})
and t.company_sid in (#{companySid})
GROUP BY t.company_sid,t.vendor_sid,t.document_type
