mapper.select:



        and (t.client_id
        like concat('%',
        #{item}, '%')
    )


        and t.product_check_sid =#{productCheckSid}


        and t.product_sid =#{productSid}


        and (t.sample_code
        like concat('%',
        #{item}, '%')
    )


        and t.sku1_sid =#{sku1Sid}


        and (t.composition
        like concat('%',
        #{item}, '%')
    )


        and (t.creator_account
        like concat('%',
        #{item}, '%')
    )


        and date_format(t.create_date,'%y%m%d') >= date_format(#{beginTime},'%y%m%d')


        and date_format(t.create_date,'%y%m%d') <= date_format(#{endTime},'%y%m%d')


        and (t.updater_account
        like concat('%',
        #{item}, '%')
    )


        and t.update_date = #{updateDate}


        and (t.data_source_sys
        like concat('%',
        #{item}, '%')
    )


        ${params.dataScope}


order by t1.material_code

mapper.update:
update s_qua_product_check_products
client_id = #{clientId}, product_check_products_sid = #{productCheckProductsSid}, product_check_sid = #{productCheckSid}, product_sid = #{productSid}, sample_code = #{sampleCode}, sku1_sid = #{sku1Sid}, composition = #{composition}, remark = #{remark}, creator_account = #{creatorAccount}, create_date = #{createDate}, updater_account = #{updaterAccount}, update_date = #{updateDate}, data_source_sys = #{dataSourceSys},
where product_check_products_sid = #{productCheckProductsSid}

update.foreach:
update s_qua_product_check_products
client_id = #{clientId}, product_check_products_sid = #{productCheckProductsSid}, product_check_sid = #{productCheckSid}, product_sid = #{productSid}, sample_code = #{sampleCode}, sku1_sid = #{sku1Sid}, composition = #{composition}, remark = #{remark}, creator_account = #{creatorAccount}, create_date = #{createDate}, updater_account = #{updaterAccount}, update_date = #{updateDate}, data_source_sys = #{dataSourceSys},
where product_check_products_sid = #{productCheckProductsSid}
