mapper.select:



        and t.client_id = #{clientId}


        and t.pur_inspection_record_sid = #{purInspectionRecordSid}


        and t.product_check_record_sid in
        (#{item})


        and t.pur_inspection_record_code like concat('%', #{purInspectionRecordCode}, '%')


        and t.product_season_sid = #{productSeasonSid}


        and t7.product_season_sid in
        (#{item})


        and t.product_season_code = #{productSeasonCode}


        and t.material_sid = #{materialSid}


        and t.material_code like concat('%', #{materialCode}, '%')


        and t5.material_name like concat('%', #{materialName}, '%')


        and t.vendor_sid = #{vendorSid}


        and t9.vendor_sid in
        (#{item})


        and t.vendor_code like concat('%', #{vendorCode}, '%')


        and t.company_sid = #{companySid}


        and t8.company_sid in
        (#{item})


        and t.company_code like concat('%',#{companyCode}, '%')


        and t.purchase_order_code like concat('%',#{purchaseOrderCode} , '%')


        and t.purchase_contract_code  like concat('%',#{purchaseContractCode}, '%')

    and
        t.carrier_name like concat('%', #{carrierName}, '%')


        and t.driver like concat('%', #{driver}, '%')


        and t.driver_phone = #{driverPhone}


        and t.carrier_note_code  like concat('%',#{carrierNoteCode}, '%')


        and t.arrival_date = #{arrivalDate}


        and t.inspection_method = #{inspectionMethod}


        and t.inspection_method in
        (#{item})


        and t.result = #{result}


        and t.result in
        (#{item})


        and t.inspection_partner_type = #{inspectionPartnerType}


        and t.inspection_batch = #{inspectionBatch}


        and t.quantity = #{quantity}


        and t.bhg_quantity = #{bhgQuantity}


        and t.repeat_inspection_num = #{repeatInspectionNum}


        and t.inspector like concat('%', #{inspector}, '%')


        and t.inspection_date = #{inspectionDate}


        and date_format(t.inspection_date,'%y%m%d') >= date_format(#{inspectionDateBegin},'%y%m%d')


        and date_format(t.inspection_date,'%y%m%d') <= date_format(#{inspectionDateEnd},'%y%m%d')


        and t.inspection_remark = #{inspectionRemark}


        and t.plant_sid = #{plantSid}


        and t.plant_sid in
        (#{item})


        and t.plant_code = #{plantCode}


        and t.defect_type = #{defectType}


        and t.defect_type in
        (#{item})


        and t.defect_description = #{defectDescription}


        and t.resolve_status = #{resolveStatus}


        and t.resolve_status in
        (#{item})


        and t.solution_remark = #{solutionRemark}


        and t.picture_path = #{picturePath}


        and t.video_path = #{videoPath}


        and t.handle_status = #{handleStatus}


        and t.handle_status in
        (#{item})


        and t.creator_account = #{creatorAccount}


        and date_format(t.create_date,'%y%m%d') >= date_format(#{beginTime},'%y%m%d')


        and date_format(t.create_date,'%y%m%d') <= date_format(#{endTime},'%y%m%d')


        and t.updater_account = #{updaterAccount}


        and t.update_date = #{updateDate}


        and t.confirmer_account = #{confirmerAccount}


        and t.confirm_date = #{confirmDate}


        and t.data_source_sys = #{dataSourceSys}


        ${params.dataScope}


order by t.pur_inspection_record_code desc

