mapper.select:



        and (t.client_id
        like concat('%',
        #{item}, '%')
    )


        and t.rawmat_check_sid =#{rawmatCheckSid}


        and t.rawmat_check_code like concat('%',#{rawmatCheckCode}, '%')


        and t.product_season_sid =#{productSeasonSid}


        and t.product_season_sid in (#{item})


        and t.material_sid =#{materialSid}


        and t.sku1_sid =#{sku1Sid}


        and t.sample_category = #{sampleCategory}


        and t.sample_category in (#{item})


        and t.check_category = #{checkCategory}


        and t.check_category in (#{item})


        and t.check_type = #{checkType}


        and t.check_type in (#{item})


        and t.quantity =#{quantity}


        and t.vendor_sid =#{vendorSid}


        and t.vendor_sid in (#{item})


        and (
        t.supplier_product_code
        like concat('%',
        #{item}, '%')
    )


        and (
        t.supplier_product_color
        like concat('%',
        #{item}, '%')
    )


        and t.check_degree = #{checkDegree}


        and t.check_degree in (#{item})


        and (t.is_supplier_check
        like concat('%',
        #{item}, '%')
    )


        and t.check_batch =#{checkBatch}


        and (t.composition
        like concat('%',
        #{item}, '%')
    )


        and (t.grade
        like concat('%',
        #{item}, '%')
    )


        and (t.safe_category
        like concat('%',
        #{item}, '%')
    )


        and t.check_standard =#{checkStandard}


        and t.check_lab_sid =#{checkLabSid}


        and t.check_lab_sid in (#{item})


        and (t.iqc
        like concat('%',
        #{item}, '%')
    )


        and (t.report_code
        like concat('%',
        #{item}, '%')
    )


        and (t.result
        like concat('%',
        #{item}, '%')
    )


        and t.stock_batch =#{stockBatch}


        and t.pre_rawmat_check_sid =#{preRawmatCheckSid}


        and t.handle_status = #{handleStatus}


        and t.handle_status in (#{item})


        and (t.creator_account
        like concat('%',
        #{item}, '%')
    )


        and date_format(t.create_date,'%y%m%d') >= date_format(#{beginTime},'%y%m%d')


        and date_format(t.create_date,'%y%m%d') <= date_format(#{endTime},'%y%m%d')


        and (t.updater_account
        like concat('%',
        #{item}, '%')
    )


        and t.update_date = #{updateDate}


        and (t.confirmer_account
        like concat('%',
        #{item}, '%')
    )


        and t.confirm_date = #{confirmDate}


        and (t.data_source_sys
        like concat('%',
        #{item}, '%')
    )


        ${params.dataScope}


order by t.rawmat_check_code desc

