mapper.select:



        and (t.client_id
        like concat('%',
        #{item}, '%')
    )


        and t.attachment_sid =#{attachmentSid}


        and t.specraft_check_sid =#{specraftCheckSid}


        and (t.file_type
        like concat('%',
        #{item}, '%')
    )

    and
        t.file_name like concat('%', #{fileName}, '%')


        and (t.file_path
        like concat('%',
        #{item}, '%')
    )


        and (t.creator_account
        like concat('%',
        #{item}, '%')
    )


        and date_format(t.create_date,'%y%m%d') >= date_format(#{beginTime},'%y%m%d')


        and date_format(t.create_date,'%y%m%d') <= date_format(#{endTime},'%y%m%d')


        and (t.updater_account
        like concat('%',
        #{item}, '%')
    )


        and t.update_date = #{updateDate}


        and (t.data_source_sys
        like concat('%',
        #{item}, '%')
    )


        ${params.dataScope}


order by t.create_date desc

mapper.update:
update s_qua_specraft_check_attach
client_id = #{clientId}, attachment_sid = #{attachmentSid}, specraft_check_sid = #{specraftCheckSid}, file_type = #{fileType}, file_name = #{fileName}, file_path = #{filePath}, remark = #{remark}, creator_account = #{creatorAccount}, create_date = #{createDate}, updater_account = #{updaterAccount}, update_date = #{updateDate}, data_source_sys = #{dataSourceSys},
where attachment_sid = #{attachmentSid}

update.foreach:
update s_qua_specraft_check_attach
client_id = #{clientId}, attachment_sid = #{attachmentSid}, specraft_check_sid = #{specraftCheckSid}, file_type = #{fileType}, file_name = #{fileName}, file_path = #{filePath}, remark = #{remark}, creator_account = #{creatorAccount}, create_date = #{createDate}, updater_account = #{updaterAccount}, update_date = #{updateDate}, data_source_sys = #{dataSourceSys},
where attachment_sid = #{attachmentSid}
