mapper.select:



        and (t.client_id
        like concat('%',
        #{item}, '%')
    )


        and t.specraft_check_item_sid =#{specraftCheckItemSid}


        and t.specraft_check_sid =#{specraftCheckSid}


        and t.item_category =#{itemCategory}


        and t.item_sid =#{itemSid}


        and t.item_value = #{itemValue}


        and (t.item_result
        like concat('%',
        #{item}, '%')
    )


        and t.pre_item_value = #{preItemValue}


        and (t.pre_item_result
        like concat('%',
        #{item}, '%')
    )


        and (t.creator_account
        like concat('%',
        #{item}, '%')
    )


        and date_format(t.create_date,'%y%m%d') >= date_format(#{beginTime},'%y%m%d')


        and date_format(t.create_date,'%y%m%d') <= date_format(#{endTime},'%y%m%d')


        and (t.updater_account
        like concat('%',
        #{item}, '%')
    )


        and t.update_date = #{updateDate}


        and (t.data_source_sys
        like concat('%',
        #{item}, '%')
    )


        ${params.dataScope}


order by t1.name

mapper.update:
update s_qua_specraft_check_item
client_id = #{clientId}, specraft_check_item_sid = #{specraftCheckItemSid}, specraft_check_sid = #{specraftCheckSid}, item_category = #{itemCategory}, item_sid = #{itemSid}, item_value = #{itemValue}, item_result = #{itemResult}, pre_item_value = #{preItemValue}, pre_item_result = #{preItemResult}, remark = #{remark}, creator_account = #{creatorAccount}, create_date = #{createDate}, updater_account = #{updaterAccount}, update_date = #{updateDate}, data_source_sys = #{dataSourceSys},
where specraft_check_item_sid = #{specraftCheckItemSid}

update.foreach:
update s_qua_specraft_check_item
client_id = #{clientId}, specraft_check_item_sid = #{specraftCheckItemSid}, specraft_check_sid = #{specraftCheckSid}, item_category = #{itemCategory}, item_sid = #{itemSid}, item_value = #{itemValue}, item_result = #{itemResult}, pre_item_value = #{preItemValue}, pre_item_result = #{preItemResult}, remark = #{remark}, creator_account = #{creatorAccount}, create_date = #{createDate}, updater_account = #{updaterAccount}, update_date = #{updateDate}, data_source_sys = #{dataSourceSys},
where specraft_check_item_sid = #{specraftCheckItemSid}
