mapper.select:



        and (t.client_id
        like concat('%',
        #{item}, '%')
    )


        and t.specraft_check_sid =#{specraftCheckSid}


        and t.specraft_check_code like concat('%',#{specraftCheckCode}, '%')


        and t.product_season_sid =#{productSeasonSid}


        and t.product_season_sid in (
        #{item})


        and (t.sample_category
        like concat('%',
        #{item}, '%')
    )


        and t.sample_category in (
        #{item})


        and t.check_category = #{checkCategory}


        and t.check_category in (
        #{item})


        and t.check_type = #{checkType}


        and t.check_type in (#{item})


        and (t.check_degree
        like concat('%',
        #{item}, '%')
    )


        and t.check_degree in (#{item})


        and t.vendor_sid =#{vendorSid}


        and t.vendor_sid in (#{item})


        and (t.is_supplier_check
        like concat('%',
        #{item}, '%')
    )


        and t.check_batch =#{checkBatch}


        and (t.grade
        like concat('%',
        #{item}, '%')
    )


        and (t.safe_category
        like concat('%',
        #{item}, '%')
    )


        and t.check_standard =#{checkStandard}


        and t.check_lab_sid =#{checkLabSid}


        and t.check_lab_sid in (
        #{item})


        and (t.iqc
        like concat('%',
        #{item}, '%')
    )


        and (t.report_code
        like concat('%',
        #{item}, '%')
    )


        and (t.result
        like concat('%',
        #{item}, '%')
    )


        and t.pre_specraft_check_sid =#{preSpecraftCheckSid}


        and (t.handle_status
        like concat('%',
        #{item}, '%')
    )


        and t.handle_status in (
        #{item})


        and (t.creator_account
        like concat('%',
        #{item}, '%')
    )


        and date_format(t.create_date,'%y%m%d') >= date_format(#{beginTime},'%y%m%d')


        and date_format(t.create_date,'%y%m%d') <= date_format(#{endTime},'%y%m%d')


        and (t.updater_account
        like concat('%',
        #{item}, '%')
    )


        and t.update_date = #{updateDate}


        and (t.confirmer_account
        like concat('%',
        #{item}, '%')
    )


        and t.confirm_date = #{confirmDate}


        and (t.data_source_sys
        like concat('%',
        #{item}, '%')
    )


        ${params.dataScope}


order by t.specraft_check_code desc

mapper.update:
update s_qua_specraft_check
client_id = #{clientId}, specraft_check_sid = #{specraftCheckSid}, specraft_check_code = #{specraftCheckCode}, product_season_sid = #{productSeasonSid}, sample_category = #{sampleCategory}, check_category = #{checkCategory}, check_type = #{checkType}, check_degree = #{checkDegree}, vendor_sid = #{vendorSid}, is_supplier_check = #{isSupplierCheck}, check_batch = #{checkBatch}, grade = #{grade}, safe_category = #{safeCategory}, check_standard = #{checkStandard}, check_lab_sid = #{checkLabSid}, iqc = #{iqc}, report_code = #{reportCode}, result = #{result}, pre_specraft_check_sid = #{preSpecraftCheckSid}, remark = #{remark}, handle_status = #{handleStatus}, updater_account = #{updaterAccount}, update_date = #{updateDate}, confirmer_account = #{confirmerAccount}, confirm_date = #{confirmDate}, data_source_sys = #{dataSourceSys},
where specraft_check_sid = #{specraftCheckSid}

update.foreach:
