mapper.select:



        and t.client_id = #{clientId}


        and t2.nick_name like concat('%',  #{creatorAccountName}, '%')


        and t.customer_sid in
        (#{customerSid})


        and t.creator_account in
        (#{creatorAccount})


        and t.company_sid in
        (#{companySid})


        and t.customer_sid = #{customerSid}


        and t.company_sid = #{companySid}


        and t.material_type = #{materialType}



            and t.material_type is NULL


            and t.material_type in
            (#{item})



        and t.year_months = #{yearMonths}


        and t.initial_balance = #{initialBalance}


        and t.fahuo = #{fahuo}


        and t.kaipiao = #{kaipiao}


        and t.fukuan = #{fukuan}


        and t.shoukuan = #{shoukuan}


        and t.xiaoshoudikou = #{xiaoshoudikou}


        and t.koukuan = #{koukuan}


        and t.tiaozhang = #{tiaozhang}


        and t.ending_balance = #{endingBalance}


        and t.yajin = #{yajin}


        and t.zanyakuan = #{zanyakuan}


        and t.yue_shijijieqian = #{yueShijijieqian}


        and t.yingfuzangu = #{yingfuzangu}


        and t.yingshouzangu = #{yingshouzangu}


        and t.currency = #{currency}


        and t.currency_unit = #{currencyUnit}


        and t.handle_status = #{handleStatus}


        and t.creator_account = #{creatorAccount}


        and t.year_months >= #{beginTime}


        and t.year_months <= #{endTime}


        and t.data_source_sys = #{dataSourceSys}


        ${params.dataScope}


order by t.customer_month_account_bill_sid DESC

