SELECT t.client_id, t.sale_contract_sid, t.sale_contract_code, t.sale_person, t2.user_id as sale_person_id, t2.nick_name as sale_person_name, t3.sale_finance_account_id FROM s_sal_sale_contract t LEFT JOIN sys_user t2 ON t.sale_person = t2.user_name AND t.client_id = t2.client_id LEFT JOIN ( SELECT ta.client_id, GROUP_CONCAT(tb.user_id ORDER BY tb.user_id ASC SEPARATOR ';') as sale_finance_account_id FROM s_sys_default_setting_client ta LEFT JOIN sys_user tb ON ta.sale_finance_account = tb.user_name or ta.sale_finance_account like concat('%;', tb.user_name, ';%') or ta.sale_finance_account like concat(tb.user_name, ';%') or ta.sale_finance_account like concat('%;', tb.user_name) GROUP BY ta.client_id ) t3 ON t.client_id = t3.client_id
and t.client_id = #{clientId}
and t.handle_status = #{handleStatus}
and t.sign_in_status = #{signInStatus}
and t.upload_status = #{uploadStatus}
and t.contract_type = #{contractType}

SELECT ifnull(t.num_contract, 0) as num_contract, ifnull(t.sum_amount_tax_contract, 0) as sum_amount_tax_contract, t.year,t.customer_sid,ifnull(t.contract_type, '') as contract_type, ifnull(t1.num_order, 0) as num_order, t2.customer_name, ifnull(t2.short_name,t2.customer_name) as customer_short_name FROM ( SELECT COUNT(x.sale_contract_sid) as num_contract, SUM(x.currency_amount_tax) as sum_amount_tax_contract, x.year, x.customer_sid, x.contract_type FROM s_sal_sale_contract x WHERE x.handle_status IN ('5','9') GROUP BY x.customer_sid,x.year,x.contract_type ) t LEFT JOIN ( SELECT COUNT(distinct y.sales_order_sid) as num_order, y2.year, y2.contract_type, y.customer_sid FROM s_sal_sales_order y LEFT JOIN s_sal_sale_contract y2 ON y.sale_contract_sid = y2.sale_contract_sid WHERE y.handle_status IN ('5','7') and y2.handle_status IN ('5', '9') GROUP BY y2.customer_sid, y2.year, y2.contract_type ) t1 ON t.year = t1.year AND t.contract_type = t1.contract_type AND t.customer_sid = t1.customer_sid LEFT JOIN s_bas_customer t2 ON t.customer_sid = t2.customer_sid
and t.customer_sid in (#{customerSid})
and t.company_sid in (#{companySid})
and t.year in (#{year})
and t.contract_type in (#{contractType})
and t.year = #{year}
and t.customer_sid = #{customerSid}
and t.company_sid = #{companySid}
and t.contract_type = #{contractType}
ORDER BY t.year DESC ,t2.customer_name ASC, t.contract_type ASC

SELECT ifnull(t.num_contract, 0) as num_contract, ifnull(t.sum_amount_tax_contract, 0) as sum_amount_tax_contract, t.year,t.customer_sid,ifnull(t.contract_type,'') as contract_type, ifnull(t1.num_order, 0) as num_order, t.company_sid,t.product_season_sid,t.sale_mode,t.business_channel,t.raw_material_mode,t5.name as business_channel_name, t2.customer_name,ifnull(t2.short_name,t2.customer_name) as customer_short_name ,t3.company_name, ifnull(t3.short_name,t3.company_name) as company_short_name, ifnull(t4.product_season_name, '') as product_season_name FROM ( SELECT COUNT(x.sale_contract_sid) as num_contract, SUM(x.currency_amount_tax) as sum_amount_tax_contract,x.business_channel, x.year, x.customer_sid, x.contract_type,x.company_sid,x.product_season_sid,x.sale_mode,x.raw_material_mode FROM s_sal_sale_contract x
x.handle_status IN ('5', '9')
and x.customer_sid in (#{customerSid})
and x.company_sid in (#{companySid})
and x.year in (#{year})
and x.contract_type in (#{contractType})
and x.year = #{year}
and x.customer_sid = #{customerSid}
and x.company_sid = #{companySid}
and x.contract_type = #{contractType}
GROUP BY x.company_sid,x.product_season_sid,x.business_channel,x.sale_mode,x.raw_material_mode ) t LEFT JOIN ( SELECT COUNT(distinct y.sales_order_sid) as num_order, y2.year, y2.contract_type, y2.customer_sid, y2.company_sid,y2.product_season_sid,y2.sale_mode,y2.raw_material_mode,y2.business_channel FROM s_sal_sales_order y LEFT JOIN s_sal_sale_contract y2 ON y.sale_contract_sid = y2.sale_contract_sid
y.handle_status IN ('5', '7') AND y2.handle_status IN ('5', '9')
and y2.customer_sid in (#{customerSid})
and y2.company_sid in (#{companySid})
and y2.year in (#{year})
and y2.contract_type in (#{contractType})
and y2.year = #{year}
and y2.customer_sid = #{customerSid}
and y2.company_sid = #{companySid}
and y2.contract_type = #{contractType}
GROUP BY y2.company_sid,y2.product_season_sid,y2.business_channel,y2.sale_mode,y2.raw_material_mode ) t1 ON t.company_sid = t1.company_sid AND ((t.product_season_sid = t1.product_season_sid)or (t.product_season_sid is null and t1.product_season_sid is null )) AND t.business_channel = t1.business_channel AND ((t.sale_mode = t1.sale_mode)or (t.sale_mode is null and t1.sale_mode is null )) AND ((t.raw_material_mode = t1.raw_material_mode)or(t.raw_material_mode is null and t1.raw_material_mode is null)) LEFT JOIN s_bas_customer t2 ON t.customer_sid = t2.customer_sid LEFT JOIN s_bas_company t3 ON t.company_sid = t3.company_sid LEFT JOIN s_bas_product_season t4 ON t.product_season_sid = t4.product_season_sid LEFT JOIN s_con_business_channel t5 ON t.business_channel = t5.code ORDER BY t3.short_name ASC, t4.product_season_name ASC, t.sale_mode ASC, t5.name ASC, t.raw_material_mode ASC
