        and t.sale_intent_contract_code like concat('%', #{saleIntentContractCode}, '%')


        and t.paper_sale_intent_contract_code like concat('%', #{paperSaleIntentContractCode}, '%')


        and (t.paper_sale_intent_contract_code is null or t.paper_sale_intent_contract_code = '')


        and (t.paper_sale_intent_contract_code is not null and t.paper_sale_intent_contract_code != '')


        and t.upload_status = #{uploadStatus}


        and t.sign_in_status = #{signInStatus}


        and t.handle_status = #{handleStatus}


        and t.handle_status in
        (#{handleStatus})


        and t.creator_account = #{creatorAccount}


        and t.creator_account in
        (#{creatorAccount})


        and t2.nick_name like concat('%', #{creatorAccountName}, '%')


        and date_format(t.create_date,'%y%m%d') >= date_format(#{beginTime},'%y%m%d')


        and date_format(t.create_date,'%y%m%d') <= date_format(#{endTime},'%y%m%d')


        and t.updater_account = #{updaterAccount}


        and t.update_date = #{updateDate}


        and t.confirmer_account = #{confirmerAccount}


        and t.confirm_date = #{confirmDate}


        and t.data_source_sys = #{dataSourceSys}


        ${params.dataScope}


ORDER BY t.sales_intent_order_code DESC

