where sales_intent_order_sid = #{salesIntentOrderSid}

update.foreach:
update s_sal_sales_intent_order
sales_intent_order_code = #{o.salesIntentOrderCode}, customer_sid = #{o.customerSid}, customer_code = #{o.customerCode}, company_sid = #{o.companySid}, company_code = #{o.companyCode}, product_season_sid = #{o.productSeasonSid}, product_season_code = #{o.productSeasonCode}, document_date = #{o.documentDate}, business_type = #{o.businessType}, business_channel = #{o.businessChannel}, sale_person = #{o.salePerson}, sale_group = #{o.saleGroup}, sale_department = #{o.saleDepartment}, sale_mode = #{o.saleMode}, raw_material_mode = #{o.rawMaterialMode}, customer_businessman = #{o.customerBusinessman}, material_type = #{o.materialType}, currency = #{o.currency}, currency_unit = #{o.currencyUnit}, sale_intent_contract_sid = #{o.saleIntentContractSid}, sale_intent_contract_code = #{o.saleIntentContractCode}, paper_sale_intent_contract_code = #{o.paperSaleIntentContractCode}, upload_status = #{o.uploadStatus}, sign_in_status = #{o.signInStatus}, cancel_type = #{o.cancelType}, cancel_remark = #{o.cancelRemark}, remark = #{o.remark}, handle_status = #{o.handleStatus}, updater_account = #{o.updaterAccount}, update_date = #{o.updateDate}, confirmer_account = #{o.confirmerAccount}, confirm_date = #{o.confirmDate},
where sales_intent_order_sid = #{o.salesIntentOrderSid}

mapper.select:
SELECT t.client_id, t.sales_intent_order_sid, t.sales_intent_order_code, t.sale_person, t2.user_id as sale_person_id, t2.nick_name as sale_person_name, t3.sale_finance_account_id FROM s_sal_sales_intent_order t LEFT JOIN sys_user t2 ON t.sale_person = t2.user_name AND t.client_id = t2.client_id LEFT JOIN ( SELECT ta.client_id, GROUP_CONCAT(tb.user_id ORDER BY tb.user_id ASC SEPARATOR ';') as sale_finance_account_id FROM s_sys_default_setting_client ta LEFT JOIN sys_user tb ON ta.sale_finance_account like concat('%;', tb.user_name, ';%') or ta.sale_finance_account like concat(tb.user_name, ';%') or ta.sale_finance_account like concat('%;', tb.user_name) GROUP BY ta.client_id ) t3 ON t.client_id = t3.client_id
and t.client_id = #{clientId}
and t.handle_status = #{handleStatus}
and t.sign_in_status = #{signInStatus}
and t.upload_status = #{uploadStatus}
