SELECT t.client_id, t.sales_order_sid, t.sales_order_code, t.sale_contract_sid, t.sale_contract_code, t.sale_person, t2.user_id as sale_person_id, t2.nick_name as sale_person_name FROM s_sal_sales_order t LEFT JOIN s_sal_sale_contract t1 ON t.sale_contract_sid = t1.sale_contract_sid LEFT JOIN sys_user t2 ON t.sale_person = t2.user_name AND t.client_id = t2.client_id
and t.client_id = #{clientId}
and t.handle_status = #{handleStatus}
and t1.handle_status = #{contractHandleStatus}
and t1.contract_purpose = #{contractPurpose}

SELECT t.client_id, t.sales_order_sid, t.sales_order_code, t.sale_contract_sid, t.sale_contract_code, t.sale_person, t2.user_id as sale_person_id, t2.nick_name as sale_person_name, t3.sale_finance_account_id FROM s_sal_sales_order t LEFT JOIN sys_user t2 ON t.sale_person = t2.user_name AND t.client_id = t2.client_id LEFT JOIN ( SELECT ta.client_id, GROUP_CONCAT(tb.user_id ORDER BY tb.user_id ASC SEPARATOR ';') as sale_finance_account_id FROM s_sys_default_setting_client ta LEFT JOIN sys_user tb ON ta.sale_finance_account like concat('%;', tb.user_name, ';%') or ta.sale_finance_account like concat(tb.user_name, ';%') or ta.sale_finance_account like concat('%;', tb.user_name) GROUP BY ta.client_id ) t3 ON t.client_id = t3.client_id
and t.client_id = #{clientId}
and t.handle_status = #{handleStatus}
and t.sign_in_status = #{signInStatus}
and t.upload_status = #{uploadStatus}
