mapper.select:



        and t.client_id = #{clientId}


        and t.field_code like concat('%', #{fieldCode}, '%')


        and t.field_name like concat('%', #{fieldName}, '%')


        and t.category = #{category}


        and t.create_source = #{createSource}


        and t.status = #{status}


        and t.handle_status = #{handleStatus}


        and date_format(t.create_date,'%y%m%d') >= date_format(#{beginTime},'%y%m%d')


        and date_format(t.create_date,'%y%m%d') <= date_format(#{endTime},'%y%m%d')


        and t.handle_status in
        (#{handleStatus})



        and t.category in
        (#{category})



        and t2.nick_name like concat('%', #{creatorAccountName}, '%')


order by t.field_code desc

mapper.update:
update s_sys_authority_field
client_id = #{clientId}, field_code = #{fieldCode}, field_name = #{fieldName}, category = #{category}, create_source = #{createSource}, remark = #{remark}, handle_status = #{handleStatus}, updater_account = #{updaterAccount}, update_date = #{updateDate}, confirmer_account = #{confirmerAccount}, confirm_date = #{confirmDate}, status = #{status}
where authority_field_sid = #{authorityFieldSid}
