mapper.select:



        and t.client_id = #{clientId}


        and t.object_code like concat('%', #{objectCode}, '%')


        and t.object_name like concat('%', #{objectName}, '%')


        and t.category = #{category}


        and t.create_source = #{createSource}


        and t.handle_status = #{handleStatus}


        and t.status = #{status}


        and date_format(t.create_date,'%y%m%d') >= date_format(#{beginTime},'%y%m%d')


        and date_format(t.create_date,'%y%m%d') <= date_format(#{endTime},'%y%m%d')


        and t.handle_status in
        (#{handleStatus})


        and t.category in
        (#{category})


        and t2.nick_name like concat('%', #{creatorAccountName}, '%')


order by t.object_code desc

mapper.update:
update s_sys_authority_object
client_id = #{clientId}, object_code = #{objectCode}, object_name = #{objectName}, category = #{category}, create_source = #{createSource}, remark = #{remark}, handle_status = #{handleStatus}, status = #{status}, updater_account = #{updaterAccount}, update_date = #{updateDate}, confirmer_account = #{confirmerAccount}, confirm_date = #{confirmDate},
where authority_object_sid = #{authorityObjectSid}

update.foreach:
update s_sys_authority_object
client_id = #{clientId}, object_code = #{objectCode}, object_name = #{objectName}, category = #{category}, create_source = #{createSource}, remark = #{remark}, handle_status = #{handleStatus}, status = #{status}, updater_account = #{updaterAccount}, update_date = #{updateDate}, confirmer_account = #{confirmerAccount}, confirm_date = #{confirmDate},
where authority_object_sid = #{authorityObjectSid}
