mapper.select:



        and t.client_id = #{clientId}


        and t.document_category = #{documentCategory}


        and t.document_category in
        (#{item})

    and
        t.document_name like concat('%', #{documentName}, '%')


        and t.document_path = #{documentPath}


        and t.version_code = #{versionCode}


        and t.document_instructions = #{documentInstructions}


        and t.creator_account = #{creatorAccount}


        and t2.nick_name like concat('%', #{creatorAccountName}, '%')


        and date_format(t.create_date,'%y%m%d') >= date_format(#{beginTime},'%y%m%d')


        and date_format(t.create_date,'%y%m%d') <= date_format(#{endTime},'%y%m%d')


        and t.updater_account = #{updaterAccount}


        and t.update_date = #{updateDate}


        and t.data_source_sys = #{dataSourceSys}


        ${params.dataScope}


order by t.document_category desc, t.create_date desc

mapper.update:
update s_sys_document
client_id = #{clientId}, document_category = #{documentCategory}, document_name = #{documentName}, document_path = #{documentPath}, version_code = #{versionCode}, document_instructions = #{documentInstructions}, remark = #{remark}, updater_account = #{updaterAccount}, update_date = #{updateDate},
where document_sid = #{documentSid}

update.foreach:
update s_sys_document
client_id = #{o.clientId}, document_category = #{o.documentCategory}, document_name = #{o.documentName}, document_path = #{o.documentPath}, version_code = #{o.versionCode}, document_instructions = #{o.documentInstructions}, remark = #{o.remark}, updater_account = #{o.updaterAccount}, update_date = #{o.updateDate},
where document_sid = #{o.documentSid}
