mapper.select:



       and t.handle_status = #{handleStatus}


        and t.status = #{status}


        and t.bom_position_sid like concat("%" , #{bomPositionSid} , "%")


        and t.bom_position_code like concat("%" , #{bomPositionCode} , "%")


        and (
                 t.up_down_suit = #{item}
             )


        and t.bom_position_name like concat('%', #{bomPositionName}, '%')


        and (
        t.bom_posit
        ion_type like concat('%' , #{item} , '%')
             )


        and t.company_sid =#{companySid}


        and (
              t.picture_path = #{item}
    )


        and t.status = #{status}


        and t.handle_status in
        (
             #{item}
        )


        and t2.nick_name like concat("%" , #{creatorAccountName} , "%")


        and t.creator_account like concat("%" , #{creatorAccount} , "%")


        and date_format(t.create_date,'%y%m%d') >= date_format(#{beginTime},'%y%m%d')


        and date_format(t.create_date,'%y%m%d') <= date_format(#{endTime},'%y%m%d')


        and (
                      t.updater_account like concat('%' , #{item} , '%')
             )


        and t.update_date = #{updateDate}


        and (
                      t.confirmer_account like concat('%' , #{item} , '%')
             )


        and t.confirm_date = #{confirmDate}


        and (
                      t.data_source_sys like concat('%' , #{item} , '%')
    )


        ${params.dataScope}


order by t.bom_position_code desc

mapper.update:
update s_tec_bom_position
client_id = #{clientId}, bom_position_sid = #{bomPositionSid}, bom_position_code = #{bomPositionCode}, bom_position_name = #{bomPositionName}, bom_position_type = #{bomPositionType}, company_sid = #{companySid}, picture_path = #{picturePath}, remark = #{remark}, status = #{status}, handle_status = #{handleStatus}, creator_account = #{creatorAccount}, create_date = #{createDate}, updater_account = #{updaterAccount}, update_date = #{updateDate}, confirmer_account = #{confirmerAccount}, confirm_date = #{confirmDate}, data_source_sys = #{dataSourceSys}, up_down_suit = #{upDownSuit}, position_description = #{positionDescription}
where bom_position_sid = #{bomPositionSid}

