line_position_sid = #{linePositionSid}, line_position_code = #{linePositionCode}, line_position_name = #{linePositionName}, measure_description = #{measureDescription}, line_position_category = #{linePositionCategory}, unit = #{unit}, picture_path = #{picturePath}, up_down_suit = #{upDownSuit}, remark = #{remark}, disable_remark = #{disableRemark}, status = #{status}, handle_status = #{handleStatus}, updater_account = #{updaterAccount}, update_date = #{updateDate}, confirmer_account = #{confirmerAccount}, confirm_date = #{confirmDate}, data_source_sys = #{dataSourceSys},
where line_position_sid = #{linePositionSid}

