mapper.select:



        and t.material_pos_infor_sid =#{materialPosInforSid}


        and t.material_size_sid =#{materialSizeSid}


        and t.model_position_sid =#{modelPositionSid}

    and
        t.model_position_name like concat('%', #{modelPositionName}, '%')


        and (
        t.measure_description like concat('%',
        #{item}, '%')
    )


        and t.deviation = #{deviation}


        and t.deviation_minus = #{deviationMinus}


        and t.serial_num =#{serialNum}


        and (t.unit like concat('%',
        #{item}, '%')
    )


        and (t.creator_account like
        concat('%',
        #{item}, '%')
    )


        and date_format(t.create_date,'%y%m%d') >= date_format(#{beginTime},'%y%m%d')


        and date_format(t.create_date,'%y%m%d') <= date_format(#{endTime},'%y%m%d')


        and t.updater_account = #{updaterAccount}


        and t.update_date = #{updateDate}


        and (t.data_source_sys like
        concat('%',
        #{item}, '%')
    )


order by t.serial_num asc, t1.model_position_code asc

mapper.update:
update s_tec_material_pos_infor_down
material_pos_infor_sid = #{materialPosInforSid}, material_size_sid = #{materialSizeSid}, model_position_sid = #{modelPositionSid}, model_position_name = #{modelPositionName}, measure_description = #{measureDescription}, deviation = #{deviation}, deviation_minus = #{deviationMinus}, serial_num = #{serialNum}, unit = #{unit}, remark = #{remark}, creator_account = #{creatorAccount}, create_date = #{createDate}, updater_account = #{updaterAccount}, update_date = #{updateDate}, data_source_sys = #{dataSourceSys},
where client_id = #{clientId}

