mapper.select:



        and (t.client_id like concat('%',
        #{item}, '%')
    )


        and t.line_position_sid in( #{item})


        and t.model_line_sid =#{modelLineSid}


        and t.model_sid =#{modelSid}


        and t.line_position_sid =#{linePositionSid}


        and t.quantity = #{quantity}


        and (
        t.line_position_category like concat('%',
        #{item}, '%')
    )


        and (t.unit like concat('%',
        #{item}, '%')
    )

    and
        t1.line_position_name like concat('%', #{linePositionName}, '%')


        and t.serial_num =#{serialNum}


        and (
        t.measure_description like concat('%',
        #{item}, '%')
    )


        and (t.creator_account like
        concat('%',
        #{item}, '%')
    )


        and date_format(t.create_date,'%y%m%d') >= date_format(#{beginTime},'%y%m%d')


        and date_format(t.create_date,'%y%m%d') <= date_format(#{endTime},'%y%m%d')


        and (t.updater_account like
        concat('%',
        #{item}, '%')
    )


        and t.update_date = #{updateDate}


        and (t.data_source_sys like
        concat('%',
        #{item}, '%')
    )


        ${params.dataScope}


order by t.serial_num asc, t1.line_position_code asc

mapper.update:
update s_tec_model_line_pos
model_line_pos_sid = #{modelLinePosSid}, model_line_sid = #{modelLineSid}, model_sid = #{modelSid}, line_position_sid = #{linePositionSid}, quantity = #{quantity}, line_position_category = #{linePositionCategory}, unit = #{unit}, line_position_name = #{linePositionName}, serial_num = #{serialNum}, remark = #{remark}, measure_description = #{measureDescription}, updater_account = #{updaterAccount}, update_date = #{updateDate}, data_source_sys = #{dataSourceSys},
where model_line_pos_sid = #{modelLinePosSid}

update.foreach:
update s_tec_model_line_pos
client_id = #{clientId}, model_line_pos_sid = #{modelLinePosSid}, model_line_sid = #{modelLineSid}, model_sid = #{modelSid}, line_position_sid = #{linePositionSid}, quantity = #{quantity}, line_position_category = #{linePositionCategory}, unit = #{unit}, line_position_name = #{linePositionName}, serial_num = #{serialNum}, remark = #{remark}, measure_description = #{measureDescription}, creator_account = #{creatorAccount}, create_date = #{createDate}, updater_account = #{updaterAccount}, update_date = #{updateDate}, data_source_sys = #{dataSourceSys},
where model_line_pos_sid = #{modelLinePosSid}
