mapper.select:
SELECT t.model_sid, t.model_code, t.model_name, t.sku_group_sid, t.down_sku_group_sid, t.is_create_model_line, t.status, t.handle_status, t1.sku_group_name FROM s_tec_model t LEFT JOIN s_bas_sku_group t1 ON t.sku_group_sid = t1.sku_group_sid
and t.up_down_suit = #{upDownSuit}
and t.male_female_flag = #{maleFemaleFlag}
and t.status = #{status}
and t.handle_status = #{handleStatus}
and t.is_create_model_line = #{isCreateModelLine}
ORDER BY t.model_name ASC

SELECT t.serial_num, t.model_position_sid, t3.model_position_code, t3.model_position_name, t3.measure_description, t.deviation, t.deviation_minus, t4.sku_name, t2.size_value, t4.sku_sid FROM s_tec_model_pos_infor t LEFT JOIN s_tec_model_pos_size t2 ON t2.model_position_infor_sid = t.model_position_infor_sid LEFT JOIN s_tec_model_position t3 ON t3.model_position_sid = t.model_position_sid LEFT JOIN s_bas_sku t4 ON t4.sku_sid = t2.sku_sid
and t.model_sid = #{modelSid}
order by t.serial_num is null, t.serial_num asc , t3.model_position_code asc, t4.sku_name ASC

