mapper.select:



        and (t.client_id like concat('%',
        #{item}, '%')
    )


        and t.model_sid = #{modelSid}


        and t.model_position_sid = #{modelPositionSid}

    and
        t.model_position_name like concat('%', #{modelPositionName}, '%')


        and (
        t.measure_description like concat('%',
        #{item}, '%')
    )


        and t.deviation = #{deviation}


        and t.deviation_minus = #{deviationMinus}


        and t.serial_num =#{serialNum}


        and (t.unit like concat('%',
        #{item}, '%')
    )


        and (t.creator_account like
        concat('%',
        #{item}, '%')
    )


        and date_format(t.create_date,'%y%m%d') >= date_format(#{beginTime},'%y%m%d')


        and date_format(t.create_date,'%y%m%d') <= date_format(#{endTime},'%y%m%d')


        and (t.updater_account like
        concat('%',
        #{item}, '%')
    )


        and t.update_date = #{updateDate}


        and (t.data_source_sys like
        concat('%',
        #{item}, '%')
    )


order by t.serial_num asc, stmp.model_position_code asc

mapper.update:
update s_tec_model_pos_infor_down
client_id = #{clientId}, model_sid = #{modelSid}, model_position_sid = #{modelPositionSid}, model_position_name = #{modelPositionName}, measure_description = #{measureDescription}, deviation = #{deviation}, deviation_minus = #{deviationMinus}, serial_num = #{serialNum}, unit = #{unit}, remark = #{remark}, creator_account = #{creatorAccount}, create_date = #{createDate}, updater_account = #{updaterAccount}, update_date = #{updateDate}, data_source_sys = #{dataSourceSys},
where model_position_infor_sid = #{modelPositionInforSid}

update.foreach:
update s_tec_model_pos_infor_down
client_id = #{clientId}, model_sid = #{modelSid}, model_position_sid = #{modelPositionSid}, model_position_name = #{modelPositionName}, measure_description = #{measureDescription}, deviation = #{deviation}, deviation_minus = #{deviationMinus}, serial_num = #{serialNum}, unit = #{unit}, remark = #{remark}, creator_account = #{creatorAccount}, create_date = #{createDate}, updater_account = #{updaterAccount}, update_date = #{updateDate}, data_source_sys = #{dataSourceSys},
where model_position_infor_sid = #{modelPositionInforSid}
