mapper.select:



        and (t.client_id like concat('%',
        #{item}, '%')
    )


        and t.model_position_infor_sid =#{modelPositionInforSid}


        and t.sku_sid = #{skuSid}


        and t.size_value = #{sizeValue}


        and (t.creator_account like
        concat('%',
        #{item}, '%')
    )


        and date_format(t.create_date,'%y%m%d') >= date_format(#{beginTime},'%y%m%d')


        and date_format(t.create_date,'%y%m%d') <= date_format(#{endTime},'%y%m%d')


        and (t.updater_account like
        concat('%',
        #{item}, '%')
    )


        and t.update_date = #{updateDate}


        and (t.data_source_sys like
        concat('%',
        #{item}, '%')
    )


order by t1.sku_name asc

mapper.update:
update s_tec_model_pos_size_down
client_id = #{clientId}, model_position_infor_sid = #{modelPositionInforSid}, sku_sid = #{skuSid}, size_value = #{sizeValue}, remark = #{remark}, creator_account = #{creatorAccount}, create_date = #{createDate}, updater_account = #{updaterAccount}, update_date = #{updateDate}, data_source_sys = #{dataSourceSys},
where model_position_size_sid = #{modelPositionSizeSid}

update.foreach:
update s_tec_model_pos_size_down
client_id = #{clientId}, model_position_infor_sid = #{modelPositionInforSid}, sku_sid = #{skuSid}, size_value = #{sizeValue}, remark = #{remark}, creator_account = #{creatorAccount}, create_date = #{createDate}, updater_account = #{updaterAccount}, update_date = #{updateDate}, data_source_sys = #{dataSourceSys},
where model_position_size_sid = #{modelPositionSizeSid}
