mapper.select:



        and ( t.client_id like concat('%',
        #{item}, '%')
    )


        and ( t.group_code like concat('%',
        #{item}, '%')
    )

    and
        t.group_name like concat('%', #{groupName}, '%')


        and ( t.up_down_suit like concat('%',
        #{item}, '%')
    )


        and ( t.customer_sid like concat('%',
        #{item}, '%')
    )


        and ( t.status like concat('%',
        #{item}, '%')
    )


        and ( t.handle_status like concat('%',
        #{item}, '%')
    )


        and ( t3.nick_name like concat('%',
        #{item}, '%')
    )


        and date_format(t.create_date,'%y%m%d') >= date_format(#{beginTime},'%y%m%d')


        and date_format(t.create_date,'%y%m%d') <= date_format(#{endTime},'%y%m%d')


        and ( t.updater_account like concat('%',
        #{item}, '%')
    )


        and t.update_date = #{updateDate}


        and ( t.confirmer_account like concat('%',
        #{item}, '%')
    )


        and t.confirm_date = #{confirmDate}


        and ( t.data_source_sys like concat('%',
        #{item}, '%')
    )


        ${params.dataScope}


order by t.group_code desc

mapper.update:
update s_tec_model_position_group
client_id = #{clientId}, group_code = #{groupCode}, group_name = #{groupName}, up_down_suit = #{upDownSuit}, customer_sid = #{customerSid}, remark = #{remark}, disable_remark = #{disableRemark}, status = #{status}, handle_status = #{handleStatus}, updater_account = #{updaterAccount}, update_date = #{updateDate}, confirmer_account = #{confirmerAccount}, confirm_date = #{confirmDate}, data_source_sys = #{dataSourceSys},
where group_sid = #{groupSid}

update.foreach:
update s_tec_model_position_group
client_id = #{clientId}, group_code = #{groupCode}, group_name = #{groupName}, up_down_suit = #{upDownSuit}, customer_sid = #{customerSid}, remark = #{remark}, disable_remark = #{disableRemark}, status = #{status}, handle_status = #{handleStatus}, updater_account = #{updaterAccount}, update_date = #{updateDate}, confirmer_account = #{confirmerAccount}, confirm_date = #{confirmDate}, data_source_sys = #{dataSourceSys},
where group_sid = #{groupSid}
