mapper.select:



        and t2.vendor_sid = #{vendorSid}


        and  m2.handle_status = #{handleStatus}


        and t.product_sid=#{materialSid}


        and m1.product_sku_sid=#{sku1Sid}


        and t2.material_type=#{materialType}


        and t2.material_category= #{materialCategory}


        and t2.purchase_type= #{purchaseType}


        and t2.touse_produce_stage= #{touseProduceStage}


        and t2.material_code like concat('%',
        #{materialCode}, '%')


order by t2.material_code desc

mapper.update:
update s_tec_product_line
product_line_sid = #{productLineSid}, product_sid = #{productSid}, remark = #{remark}, handle_status = #{handleStatus}, updater_account = #{updaterAccount}, update_date = #{updateDate}, confirmer_account = #{confirmerAccount}, confirm_date = #{confirmDate}, data_source_sys = #{dataSourceSys},
where product_line_sid = #{productLineSid}

update.foreach:
