mapper.select:



        and (t.client_id like concat('%',
        #{item}, '%')
    )


        and t.linepos_mat_sid =#{lineposMatSid}


        and t.product_line_sid =#{productLineSid}


        and t.product_sid =#{productSid}


        and t.line_position_sid =#{linePositionSid}


        and t.line_position_sid in( #{item})


        and t.material_sid =#{materialSid}


        and t.quantity = #{quantity}


        and (
        t.line_position_category like concat('%',
        #{item}, '%')
    )


        and (t.quantity_unit like
        concat('%',
        #{item}, '%')
    )

    and
        t.line_position_name like concat('%', #{linePositionName}, '%')


        and t.serial_num =#{serialNum}


        and (
        t.measure_description like concat('%',
        #{item}, '%')
    )


        and (t.creator_account like
        concat('%',
        #{item}, '%')
    )


        and date_format(t.create_date,'%y%m%d') >= date_format(#{beginTime},'%y%m%d')


        and date_format(t.create_date,'%y%m%d') <= date_format(#{endTime},'%y%m%d')


        and (t.updater_account like
        concat('%',
        #{item}, '%')
    )


        and t.update_date = #{updateDate}


        and (t.data_source_sys like
        concat('%',
        #{item}, '%')
    )


        ${params.dataScope}


order by t.serial_num asc

mapper.update:
update s_tec_product_linepos_mat
linepos_mat_sid = #{lineposMatSid}, product_line_sid = #{productLineSid}, product_sid = #{productSid}, line_position_sid = #{linePositionSid}, material_sid = #{materialSid}, quantity = #{quantity}, line_position_category = #{linePositionCategory}, quantity_unit = #{quantityUnit}, line_position_name = #{linePositionName}, serial_num = #{serialNum}, remark = #{remark}, measure_description = #{measureDescription}, updater_account = #{updaterAccount}, update_date = #{updateDate}, data_source_sys = #{dataSourceSys},
where linepos_mat_sid = #{lineposMatSid}

update.foreach:
update s_tec_product_linepos_mat
client_id = #{clientId}, linepos_mat_sid = #{lineposMatSid}, product_line_sid = #{productLineSid}, product_sid = #{productSid}, line_position_sid = #{linePositionSid}, material_sid = #{materialSid}, quantity = #{quantity}, line_position_category = #{linePositionCategory}, quantity_unit = #{quantityUnit}, line_position_name = #{linePositionName}, serial_num = #{serialNum}, remark = #{remark}, measure_description = #{measureDescription}, creator_account = #{creatorAccount}, create_date = #{createDate}, updater_account = #{updaterAccount}, update_date = #{updateDate}, data_source_sys = #{dataSourceSys},
where linepos_mat_sid = #{lineposMatSid}
