mapper.select:



        and (t.client_id like concat('%',
        #{item}, '%')
    )


        and t.account_method_group_sid =#{accountMethodGroupSid}


        and (t.account_category like
        concat('%',
        #{item}, '%')
    )


        and (t.payment_method like
        concat('%',
        #{item}, '%')
    )


        and t.rate = #{rate}


        and t.account_valid_days =#{accountValidDays}


        and (t.day_type like concat('%',
        #{item}, '%')
    )


        and t.item_sort =#{itemSort}


        and (t1.nick_name like
        concat('%',
        #{item}, '%')
    )


        and date_format(t.create_date,'%y%m%d') >= date_format(#{beginTime},'%y%m%d')


        and date_format(t.create_date,'%y%m%d') <= date_format(#{endTime},'%y%m%d')


        and (t.updater_account like
        concat('%',
        #{item}, '%')
    )


        and t.update_date = #{updateDate}


        and (t.confirmer_account
        like concat('%',
        #{item}, '%')
    )


        and t.confirm_date = #{confirmDate}


        and (t.data_source_sys like
        concat('%',
        #{item}, '%')
    )


        and t.sort =#{sort}


        ${params.dataScope}


order by t.sort asc

mapper.update:
update s_con_account_method_group_method
client_id = #{clientId}, account_method_group_sid = #{accountMethodGroupSid}, account_category = #{accountCategory}, payment_method = #{paymentMethod}, rate = #{rate}, account_valid_days = #{accountValidDays}, day_type = #{dayType}, item_sort = #{itemSort}, remark = #{remark}, creator_account = #{creatorAccount}, create_date = #{createDate}, updater_account = #{updaterAccount}, update_date = #{updateDate}, confirmer_account = #{confirmerAccount}, confirm_date = #{confirmDate}, data_source_sys = #{dataSourceSys}, sort = #{sort},
where sid = #{sid}

update.foreach:
